#@#TRUNCATE TABLE `!#prefix#!functions`;
#@#INSERT INTO `!#prefix#!functions` (`id`, `name`, `code`, `type`, `filetouse`, `active`, `mainmodule`) VALUES 
(1, 'Logon Box', '<div class="sidebox">\r\n{if $islogged == false}\r\n<form id="logon" method="post" action="{$result}">\r\n  Username <br /><input type="text" name="username" id="sideuser" maxlength="50" class="inputbox" /><br />\r\n			Password <br /><input type="password" name="password" id="sidepass" maxlength="50" class="inputbox" /><br />\r\n				<input type="submit" name="Login" id="Login" value="Login" class="button" /><br />\r\n<a href="index.php?page=forgot">I forgot my password!</a>\r\n{if $config.register == 1}<br />\r\n<a href="index.php?page=register">Register</a>{/if}\r\n</form>\r\n{else}\r\n <span>Logged in as {$uname}<br /><a href="index.php?action=logout">Logout</a></span>\r\n{/if}\r\n</div>', 1, 'logon', 1, 'frontpage'),
(2, 'Photo Album', 'photos', 2, '', 1, ''),
(4, 'Main Page', 'frontpage', 2, '', 1, ''),
(5, 'Group Link Box', '', 1, 'patrolbox', 1, 'frontpage'),
(28, 'Group Home Page', '', 4, '', 1, ''),
(29, 'Group Points', 'points', 2, '', 1, ''),
(8, 'News Box', '<div class="sidebox">\r\n{if $numnewsbox> 0}\r\n  {section name=news loop=$numnewsbox}	\r\n    <div><a href="index.php?page=news&amp;id={$newsbox[news].id}">{$newsbox[news].title}</a></div>\r\n  {/section}\r\n<div><a href="index.php?page=news">News Archive</a></div>\r\n{else}\r\n<span>No News Available</span>\r\n{/if}{if $newsboxrss == 0 && $uname != "Guest"}<a href="index.php?page=rss&amp;action=add&amp;type=6&amp;id=1"><img border="0" src="{$templateinfo.imagedir}rss_add.png" alt="Add to RSS feed" title="Add to RSS feed" /></a>&nbsp;{elseif $newsboxrss == 1 && $uname != "Guest"}<a href="index.php?page=rss&amp;action=delete&amp;type=6&amp;id=1"><img border="0" src="{$templateinfo.imagedir}rss_delete.png" alt="Remove from RSS feed" title="Remove from RSS feed" /></a>&nbsp;{/if}\r\n</div>', 1, 'newsbox', 1, 'news'),
(9, 'Downloads', 'downloads', 2, '', 1, ''),
(10, 'Calendar', 'calender', 2, '', 1, ''),
(11, 'Articles', 'patrolarticle', 2, '', 1, ''),
(12, 'Search Site', 'search', 2, '', 1, ''),
