(33, 'Online Users', '<div class="sidebox">\r\n<strong>{$userlist[0]}</strong> Registered {if $userlist[0] != 1}Users{else}User{/if}<br /><strong>{$userlist[1]}</strong> {if $userlist[1] != 1}Guests{else}Guest{/if}\r\n{if $userlist[0] > 0}<br /><br />\r\n<strong>Users Online:</strong><br />\r\n{section name=users loop=$userlist[0]+2 start=2}\r\n<a href="index.php?page=profile&amp;action=view&amp;user={$userlist[users].id}" title="View User Profile">{$userlist[users].uname}</a>{if $smarty.section.users.index == $userlist[0]+1 || $userlist[0] <= 1}{else}, {/if}\r\n {if $smarty.section.users.iteration % 2 == 0}<br />{/if}\r\n{/section}{/if}\r\n</div>', 1, 'onlineusers', 1, 'frontpage'),
(38, 'Post Poll Allowed', 'addpoll', 3, '', 1, ''),
(34, 'Award Scheme Viewer', 'advancements', 2, '', 1, ''),
(35, 'Latest Forum Posts', '<div class="sidebox">\r\n{if $numlatestposts> 0}\r\n  {section name=latestposts loop=$numlatestposts}	\r\n    <div><a href="index.php?page=forums&amp;action=topic&amp;t={$latestposts[latestposts].id}">{$latestposts[latestposts].subject|truncate:50}</a></div>\r\n  {/section}\r\n{else}\r\nNo posts in the forum\r\n{/if}\r\n</div>', 1, 'latestpost', 1, 'forums'),
(36, 'Scouts Seach Box', '<div class="sidebox">\r\n<!-- Start Of Scouts Search Code -->\r\n<form method="get" action="http://www.scoutsearch.org.uk">\r\n<img src="{$templateinfo.imagedir}scoutsearch.gif" alt="Scouts Search" /><br />\r\n<input type="text" name="qry" style="width:75%" class="inputbox" /><button class="searchButton" type="submit"><img src="images/search.png" alt="Go" /></button>\r\n<input type="hidden" name="from" value="{$config.siteaddress}" />\r\n<input type="hidden" name="mod" value="search" />\r\n<input type="hidden" name="loc" value="World-Wide" />\r\n</form>\r\n<a href="http://www.scoutsearch.org.uk"></a>\r\n<!-- End of Scouting Search Code -->\r\n</div>', 1, '', 1, 'frontpage'),
(37, 'Links Database', 'linksdata', 2, '', 1, ''),
(40, 'Poll Sidebox', '<div class="sidebox">\r\n{if $numsidepollitems > 0}\r\n<b>{$sideboxpoll.question}</b><br />\r\n  {if $sideexpired == false && $sideboxpoll.date_stop != '''' && $sideboxpoll.date_stop != 0}\r\nExpires: {$sideboxpoll.date_stop|date_format}<br />\r\n {/if}\r\n{if $sideboxvoted != true && $sideboxshowresult != true && $sideexpired == false}<form name="form1" method="POST">{/if}\r\n {if $sideboxvoted != true && $sideboxshowresult != true && $sideexpired == false}\r\n <select name="sidepoll" id="sidepoll" class="inputbox">\r\n {section name=poll loop=$numsidepollitems}\r\n  <option title="{$sideboxpoll.options[poll]}" value="{$sideboxpoll.options[poll]|replace:'' '':''''}" />{$sideboxpoll.options[poll]|truncate:20}</option>\r\n {/section} \r\n </select><br />\r\n {else}\r\n  {section name=poll loop=$numsidepollitems}\r\n   {assign var="name" value=$sideboxpoll.options[poll]|replace:'' '':''''}\r\n    <div><span title="{$sideboxpoll.options[poll]}">{$sideboxpoll.options[poll]|truncate:20}</span><br /><img src="bar.php?rating={math equation="(x / y) * 100" x=$sideboxpoll.results.$name y=$sideboxtotalvotes format="%.2f"}&amp;width=60&amp;red={$templateinfo.red}&amp;blue={$templateinfo.blue}&amp;green={$templateinfo.green}" alt="{$sideboxpoll.results.$name}/{$sideboxtotalvotes}" border="0" /></div>\r\n {/section}\r\n {/if}\r\n \r\n {if $sideboxvoted != true && !$sideexpired}\r\n{if $sideboxvoted != true && $sideboxshowresult != true}<input type="submit" value="Vote" class="button" name="sidepollvote" id="sidepollvote" /><br /><br /><a href="{$pollpage}&amp;sideview=1">View Results</a></form>{elseif $sideboxshowresult == true && $sideboxvoted != true && $expired == false}<a href="{$pollpage}">Vote</a>{/if}<br />\r\n {elseif $sideboxvoted == true && $usersname != "Guest" && $sideexpired == false}\r\n    Thank you for voting<br />\r\n {elseif $usersname == "Guest" && $expired == false}\r\nYou need to login to vote<br />\r\n {else}\r\nThis poll is closed<br />\r\n {/if}\r\n{else}\r\n<div>There are currently no active sidebox polls</div>\r\n{/if}\r\n<div><a href="index.php?page=polls">View All Polls</a></div>\r\n</div>', 1, 'poll', 1, 'polls'),
(41, 'Top Forum Posters', '<div class="sidebox">\r\n  {section name=topposters loop=$numposters}	\r\n    <div ><a href="index.php?page=profile&amp;action=view&amp;user={$topposter[topposters].id}" title="View User Profile">{$topposter[topposters].uname}</a> ({$topposter[topposters].numposts})</div>\r\n  {/section}\r\n </div>', 1, 'topcont', 1, 'forums'),
(42, 'Newest Users', '<div class="sidebox">\r\n  {section name=newest loop=$numnewest}	\r\n    <div><a href="index.php?page=profile&amp;action=view&amp;user={$newest[newest].id}" title="View User Profile">{$newest[newest].uname}</a></div>\r\n  {/section}\r\n</div>', 1, 'newest', 1, 'frontpage'),
(43, 'Next Events', '<div class="sidebox">\r\n{if $nextevent}\r\n{section name=events loop=$nextevent}\r\n{if $nextevent[events].detail != '''' && $nextevent[events].detail != null}<a href="index.php?page=calender&item={$nextevent[events].id}" class="navlink">{$nextevent[events].summary|truncate:23}</a>{else}{$nextevent[events].summary|truncate:23}{/if}<span class="hintanchor" title="{$nextevent[events].summary} :: <b>Start Date: </b>{$nextevent[events].startdate|date_format:"%Y-%m-%d"}<br /><b>Start Time: </b>{$nextevent[events].startdate|date_format:"%H:%M"}<br /><b>End Date: </b>{$nextevent[events].enddate|date_format:"%Y-%m-%d"}<br /><b>End Time: </b>{$nextevent[events].enddate|date_format:"%H:%M"}<br />{$nextevent[events].detail}"><img src="{$templateinfo.imagedir}information.png" alt="[i]" border="0"/></span><br />\r\n{/section}\r\n{else}\r\nNo upcoming events\r\n{/if}\r\n</div>', 1, 'nextevent', 1, 'calender'),
(45, 'News', 'news', 2, '', 1, ''),
(46, 'Statistics Box', '<div class="sidebox">\r\nArticles: {$sideboxstats.art}<br />\r\nPhotos: {$sideboxstats.photos}<br />\r\nUsers: {$sideboxstats.users}<br />\r\nPosts: {$sideboxstats.posts}<br />\r\nTopics: {$sideboxstats.topics}\r\n</div>', 1, 'stats', 1, 'statistics'),
(47, 'Group Log', 'patrollog', 4, '', 1, ''),
(48, 'Website Polls', 'polls', 2, '', 1, ''),
(49, 'Statistics', 'statistics', 2, '', 1, ''),
(52, 'Random Photo', '<div class="sidebox">\r\n{if $sideboxphoto}\r\n<a class="hintanchor" href="index.php?page=photos&amp;album={$sideboxphoto.album_id}&amp;menuid={$menuid}" title="Random Photo :: {$sideboxphoto.caption}"><img border="0" src="thumbnail.php?pic={$sideboxphoto.ID}" alt="{$sideboxphoto.caption}" title="{$sideboxphoto.caption}" /></a>\r\n{else}\r\n<span>No photographs</span>\r\n{/if}\r\n</div>', 1, 'randphoto', 1, 'photos'),
(53, 'User Control Panel', 'usercp', 2, '', 1, '');

#@#TRUNCATE TABLE `!#prefix#!pagecontent`;
#@#INSERT INTO `!#prefix#!pagecontent` (`pageid`, `pagenum`, `content`, `id`) VALUES 
(1, 1, '{if $number_of_albums > 0}\r\n        {section name=albums loop=$number_of_albums}\r\n            {if $patrolpages != 1}<div class="photoblocks"><a href="index.php?page=photos&amp;album={$albums[albums].ID}&amp;menuid={$menuid}" title="View {$albums[albums].album_name}">{$albums[albums].album_name|truncate:25}</a><br /><a href="index.php?page=photos&amp;album={$albums[albums].ID}&amp;menuid={$menuid}" title="View {$albums[albums].album_name}"><img border="0" src="thumbnail.php?pic={$albums[albums].randomphoto}" alt="View {$albums[albums].album_name}" /></a><br /><span class="smalltext">Contains {$albums[albums].numphotos} photos</span></div>\r\n            {else}<div class="photoblocks"><a href="index.php?page=patrolpages&amp;site={$siteid}&amp;patrol={$patrolid}&amp;content=photos&amp;album={$albums[albums].ID}&amp;menuid={$menuid}" title="View {$albums[albums].album_name}">{$albums[albums].album_name|truncate:25}</a><br /><a href="index.php?page=patrolpages&amp;site={$siteid}&amp;patrol={$patrolid}&amp;content=photos&amp;album={$albums[albums].ID}&amp;menuid={$menuid}" title="View {$albums[albums].album_name}"><img border="0" src="thumbnail.php?pic={$albums[albums].randomphoto}" alt="View {$albums[albums].album_name}" /></a><br /><span class="tinytext">Contains {$albums[albums].numphotos} photos</span></div>\r\n            {/if}\r\n        {/section}\r\n        <br style="clear:both" />\r\n{else}\r\n<div style="text-align:left">No photo albums available.</div>\r\n {/if}', 1),
(3, 1, '{if $mode == ''topiclist''}\r\n{if $patrolpage}\r\n{if $rss == 0 && $uname != "Guest"}<a href="index.php?page=rss&amp;action=add&amp;id={$patrolid}&amp;type=5&amp;menuid={$menuid}"><img border="0" src="{$templateinfo.imagedir}rss_add.png" alt="Add to RSS feed" title="Add to RSS feed" /></a>&nbsp;{elseif $rss == 1 && $uname != "Guest"}<a href="index.php?page=rss&amp;action=delete&amp;type=5&amp;id={$patrolid}&amp;menuid={$menuid}"><img border="0" src="{$templateinfo.imagedir}rss_delete.png" alt="Remove from RSS feed" title="Remove from RSS feed" /></a>&nbsp;{/if}\r\n{else}\r\n{if $rss == 0 && $uname != "Guest"}<a href="index.php?page=rss&amp;action=add&amp;type=4&amp;id=0&amp;menuid={$menuid}"><img border="0" src="{$templateinfo.imagedir}rss_add.png" alt="Add to RSS feed" title="Add to RSS feed" /></a>&nbsp;{elseif $rss == 1 && $uname != "Guest"}<a href="index.php?page=rss&amp;action=delete&amp;type=4&amp;id=0&amp;menuid={$menuid}"><img border="0" src="{$templateinfo.imagedir}rss_delete.png" alt="Remove from RSS feed" title="Remove from RSS feed" /></a>&nbsp;{/if}\r\n{/if}\r\n{if !$numarts && !$numtopics}\r\nThere are no articles available\r\n{/if}\r\n{if $numtopics}\r\n<ul class="list">\r\n{section name=topic loop=$numtopics}\r\n{if $topics[topic].number }\r\n<li>\r\n{if $patrolpage}\r\n<a href="index.php?page=patrolpages&amp;patrol={$patrolid}&amp;content=patrolarticle&amp;tid={$topics[topic].id}&amp;menuid={$menuid}&amp;action=topic">{$topics[topic].title}</a>{else}<a href="index.php?page=patrolarticle&amp;tid={$topics[topic].id}&amp;menuid={$menuid}&amp;action=topic">{$topics[topic].title}</a>\r\n{/if} <span class="desc2">({$topics[topic].number} items)</span><br /><span class="desc">{$topics[topic].description}</span>\r\n</li>{/if}\r\n     {/section}\r\n    </ul>\r\n{/if}\r\n{if ($numarts)} \r\n    <table border="0" cellspacing="1" cellpadding="3" class="table" width = "100%">\r\n    <tr><th colspan="3" class="normalhead">Articles without topics</th></tr>\r\n      <tr>\r\n        <th class="smallhead">Title of article</th>\r\n        <th class="smallhead">Date posted</th>\r\n        <th class="smallhead">Author of article</th>\r\n      </tr>\r\n    {section name=patrolarticles loop=$numarts}\r\n         <tr>\r\n            <td class="row">{if $patrolpage}<a href="index.php?page=patrolpages&amp;patrol={$patrolid}&amp;content=patrolarticle&amp;id={$patrolarts[patrolarticles].ID}&amp;menuid={$menuid}&amp;action=view">{$patrolarts[patrolarticles].title}</a>{else}<a href="index.php?page=patrolarticle&amp;id={$patrolarts[patrolarticles].ID}&amp;menuid={$menuid}&amp;action=view">{$patrolarts[patrolarticles].title}</a>{/if}</td>\r\n            <td class="row">{$patrolarts[patrolarticles].date_post+$timeoffset|date_format_pretty}&nbsp;</td>\r\n            <td class="row">{$patrolarts[patrolarticles].author}&nbsp;</td>\r\n       </tr>\r\n     {/section}\r\n    </table>\r\n{/if}\r\n\r\n{elseif $mode == "viewtopic"}\r\n\r\n<h2>{$topic.title}</h2>\r\n{if $numarticles > 0}\r\n{if $numpages > 1}\r\n<div style="text-align:center">\r\n        {if $prev && $patrolpage}\r\n            <a href="index.php?page=patrolpages&amp;patrol={$patrolid}&amp;content=patrolarticle&amp;start={$prev_start}&amp;tid={$topic.id}&amp;action=topic&amp;menuid={$menuid}"><img border="0" src="{$templateinfo.imagedir}prev.png" alt="Previous Page" title="Previous Page" /></a>\r\n        {else}\r\n            <a href="index.php?page=patrolarticle&amp;start={$prev_start}&amp;tid={$topic.id}&amp;action=topic&amp;menuid={$menuid}"><img border="0" src="{$templateinfo.imagedir}prev.png" alt="Previous Page" title="Previous Page" /></a>\r\n        {/if}\r\n    {if $numpages > 6}\r\n        {if $currentpage == 1 || $currentpage == $numpages}\r\n            {section name=pages loop=3}\r\n                {if $currentpage != $smarty.section.pages.iteration && $patrolpage}\r\n                    <a href="index.php?page=patrolpages&amp;patrol={$patrolid}&amp;content=patrolarticle&amp;start={$num_per_page*$smarty.section.pages.index}&amp;tid={$topic.id}&amp;action=topic&amp;menuid={$menuid}">\r\n                {elseif $currentpage != $smarty.section.pages.iteration}\r\n                    <a href="index.php?page=patrolarticle&amp;start={$num_per_page*$smarty.section.pages.index}&amp;tid={$topic.id}&amp;action=topic&amp;menuid={$menuid}">\r\n                {else}\r\n                    <span class="currpage">\r\n                {/if}{$smarty.section.pages.iteration}{if $currentpage != $smarty.section.pages.iteration}</a>{else}</span>{/if}\r\n            {/section}   \r\n....            \r\n            {section name=pages loop=$numpages+1 start=$numpages-2}\r\n                {if $currentpage != $smarty.section.pages.index && $patrolpage}\r\n                    <a href="index.php?page=patrolpages&amp;patrol={$patrolid}&amp;content=patrolarticle&amp;start={$num_per_page*$smarty.section.pages.index-$num_per_page}&amp;tid={$topic.id}&amp;action=topic&amp;menuid={$menuid}">\r\n                {elseif $currentpage != $smarty.section.pages.index}\r\n                    <a href="index.php?page=patrolarticle&amp;start={$num_per_page*$smarty.section.pages.index-$num_per_page}&amp;tid={$topic.id}&amp;action=topic&amp;menuid={$menuid}">\r\n                {else}\r\n                    <span class="currpage">\r\n                {/if}{$smarty.section.pages.index}{if $currentpage != $smarty.section.pages.index}</a>{else}</span>{/if}\r\n            {/section}        \r\n        {else}\r\n            {if $currentpage != $smarty.section.pages.index && $patrolpage}\r\n                    <a href="index.php?page=patrolpages&amp;patrol={$patrolid}&amp;content=patrolarticle&amp;start=0&amp;tid={$topic.id}&amp;action=topic&amp;menuid={$menuid}">\r\n                {elseif $currentpage != $smarty.section.pages.index}\r\n                    <a href="index.php?page=patrolarticle&amp;start=0&amp;tid={$topic.id}&amp;action=topic&amp;menuid={$menuid}">\r\n{/if}1<a/>{if $currentpage != 2 && $currentpage != 3 && $currentpage != 4}....{/if}     \r\n            {section name=pages loop=$currentpage+3 start=$currentpage-2}\r\n                {if !($smarty.section.pages.index <= 1) && !($smarty.section.pages.index >= $numpages)}\r\n                {if $currentpage != $smarty.section.pages.index && $patrolpage}\r\n                    <a href="index.php?page=patrolpages&amp;patrol={$patrolid}&amp;content=patrolarticle&amp;start={$num_per_page*$smarty.section.pages.index}&amp;tid={$topic.id}&amp;action=topic&amp;menuid={$menuid}">\r\n                {elseif $currentpage != $smarty.section.pages.index}\r\n                    <a href="index.php?page=patrolarticle&amp;start={$num_per_page*$smarty.section.pages.index-$num_per_page}&amp;tid={$topic.id}&amp;action=topic&amp;menuid={$menuid}">\r\n                {else}\r\n                    <span class="currpage">\r\n                {/if}{$smarty.section.pages.index}{if $currentpage != $smarty.section.pages.index}</a>{else}</span>{/if}{/if}\r\n            {/section}\r\n            \r\n            {if $currentpage != $numpages-1 && $currentpage != $numpages-2 && $currentpage != $numpages-3}....{/if}\r\n                {if $patrolpage}\r\n                    <a href="index.php?page=patrolpages&amp;patrol={$patrolid}&amp;content=patrolarticle&amp;start={$num_per_page*$numpages-$num_per_page}&amp;tid={$topic.id}&amp;action=topic&amp;menuid={$menuid}">\r\n                {else}\r\n                    <a href="index.php?page=patrolarticle&amp;start={$num_per_page*$numpages-$num_per_page}&amp;tid={$topic.id}&amp;action=topic&amp;menuid={$menuid}">\r\n                {/if}\r\n            {$numpages}<a/>\r\n        {/if}\r\n    {else}\r\n    {section name=pages loop=$numpages}\r\n        {if $currentpage != $smarty.section.pages.iteration && $patrolpage}\r\n            <a href="index.php?page=patrolpages&amp;patrol={$patrolid}&amp;content=patrolarticle&amp;start={$num_per_page*$smarty.section.pages.index}&amp;tid={$topic.id}&amp;action=topic&amp;menuid={$menuid}">\r\n        {elseif $currentpage != $smarty.section.pages.iteration}\r\n            <a href="index.php?page=patrolarticle&amp;start={$num_per_page*$smarty.section.pages.index}&amp;tid={$topic.id}&amp;action=topic&amp;menuid={$menuid}">\r\n        {else}\r\n            <span class="currpage">\r\n        {/if}{$smarty.section.pages.iteration}{if $currentpage != $smarty.section.pages.iteration}</a>{else}</span>{/if}\r\n    {/section}\r\n    {/if}\r\n    {if $next && $patrolpage}\r\n        <a href="index.php?page=patrolpages&amp;patrol={$patrolid}&amp;content=patrolarticle&amp;start={$next_start}&amp;tid={$topic.id}&amp;action=topic&amp;menuid={$menuid}"><img border="0" src="{$templateinfo.imagedir}next.png" alt="Next Page" title="Next Page" /></a>\r\n    {elseif $next}\r\n        <a href="index.php?page=patrolarticle&amp;start={$next_start}&amp;tid={$topic.id}&amp;action=topic&amp;menuid={$menuid}"><img border="0" src="{$templateinfo.imagedir}next.png" alt="Next Page" title="Next Page" /></a>\r\n    {/if}\r\n    </div>\r\n{/if}\r\n{if $topic.display == 1}\r\n    <table border="0" cellspacing="1" cellpadding="3" class="table" width = "100%">\r\n      <tr>\r\n        <th class="smallhead">Title of article</th>\r\n        <th class="smallhead">Date posted</th>\r\n        <th class="smallhead">Author of article</th>\r\n      </tr>\r\n    {section name=patrolarticles loop=$numarticles}\r\n         <tr>\r\n            <td class="row">{if $patrolpage}<a href="index.php?page=patrolpages&amp;patrol={$patrolid}&amp;content=patrolarticle&amp;id={$articles[patrolarticles].ID}&amp;menuid={$menuid}&amp;tid={$topic.id}&amp;action=view">{$articles[patrolarticles].title}</a>{else}<a href="index.php?page=patrolarticle&amp;id={$articles[patrolarticles].ID}&amp;menuid={$menuid}&amp;tid={$topic.id}&amp;action=view">{$articles[patrolarticles].title}</a>{/if}</td>\r\n            <td class="row">{$articles[patrolarticles].