(13, 1, '<form method="post">\r\n<div style="clear:both;"></div>\r\n    <div align="center"><input type="text" name="search" id="search" {if $search != ""}value=''{$search}''{else}value="Enter search item..."{/if}  onblur="if(this.value=='''') this.value=''Enter search item...'';" onfocus="if(this.value==''Enter search item...'') this.value='''';"  class="inputbox" /></div>\r\n    <div align="center">\r\n    <label for="content"><input type="checkbox" value="1" id="content" name="content" {if $contentcheck || $searched == 0}checked="checked"{/if}/>Content</label>&nbsp;\r\n    <label for="forum"><input type="checkbox" value="1" id="forum" name="forum" {if $forumcheck}checked="checked"{/if}/>Forum Posts</label>&nbsp;\r\n    <label for="article"><input type="checkbox" value="1" id="article" name="article"  {if $articlecheck}checked="checked"{/if}/>Articles</label>&nbsp;\r\n    <label for="news"><input type="checkbox" value="1" id="news" name="news"  {if $newscheck}checked="checked"{/if}/>News</label>&nbsp;\r\n    <label for="events"><input type="checkbox" value="1" id="events" name="events"  {if $eventscheck}checked="checked"{/if}/>Events</label>&nbsp;\r\n    <label for="pm"><input type="checkbox" value="1" id="pm" name="pm"  {if $pmcheck}checked="checked"{/if}/>Personal Messages</label>\r\n    <br /><input type="submit" name="Submit" id="Submit"  value="Search" class="button" />\r\n    <br />\r\n    <span class="smalltext">To search for a phrase place it into double quotation marks (Eg. "Scouts is fun")</span>\r\n</div>\r\n</form>\r\n{if $searched == 1}\r\n{if $results}\r\n    <div style="font-weight:bold">You search ({$term_list}) returned {$results} matches:</div>\r\n    {if $numcontentresults}\r\n    {section name=results loop=$numcontentresults}\r\n     <div>\r\n     {if $contentresults[results].itemtype == 1}\r\n     \r\n	<h3 style="text-align:left;margin:0px;padding:0px;"><a href="index.php?page={$contentresults[results].id}&amp;type=static&amp;menuid={$menuid}">{$contentresults[results].friendly|strip_tags:false}</a></h3>\r\n	<span class="smalltext"><b>Item Type: </b>Static Content&nbsp;|&nbsp;<b>Relevance: </b>{$contentresults[results].score|round}</span>\r\n	<div>{$contentresults[results].content|strip_tags|truncate:300}<br /><span class="smalltext"><a href="index.php?page={$contentresults[results].id}&amp;type=static&amp;menuid={$menuid}">Read more...</a></span></div>\r\n	\r\n     {elseif $contentresults[results].itemtype == 2}   \r\n     \r\n	<h3 style="text-align:left;margin:0px;padding:0px;"><a  href="index.php?page=patrolpages&patrol={$contentresults[results].pid}&amp;content={$contentresults[results].id}&amp;menuid={$menuid}">{$contentresults[results].friendly|strip_tags:false}</a></h3>\r\n	<span class="smalltext"><b>Item Type: </b>Group Page for {$contentresults[results].groupname}&nbsp;|&nbsp;<b>Relevance: </b>{$contentresults[results].score|round}</span>\r\n		<div>{$contentresults[results].content|strip_tags|truncate:300}<br /><span class="smalltext"><a href="index.php?page=patrolpages&patrol={$contentresults[results].pid}&amp;content={$contentresults[results].id}&amp;menuid={$menuid}">Read more...</a></span></div>\r\n		\r\n    {elseif $contentresults[results].itemtype == 3}\r\n    \r\n	<h3 style="text-align:left;margin:0px;padding:0px;"><a  href="index.php?page=patrolarticle&id={$contentresults[results].ID}&amp;action=view&amp;menuid={$menuid}">{$contentresults[results].title|strip_tags:false}</a></h3>\r\n	<span class="smalltext"><b>Item Type: </b>Article&nbsp;|&nbsp;<b>Relevance: </b>{$contentresults[results].score|round}</span>\r\n		<div>{if $contentresults[results].summary != ''''}{$contentresults[results].summary|truncate:300}{else}{$contentresults[results].detail|strip_tags|truncate:300}{/if}<br /><span class="smalltext"><a href="index.php?page=patrolarticle&id={$contentresults[results].ID}&amp;action=view&amp;menuid={$menuid}">Read more...</a></span></div>\r\n		\r\n     {elseif $contentresults[results].itemtype == 4}\r\n     \r\n	<h3 style="text-align:left;margin:0px;padding:0px;"><a  href="index.php?page=forums&amp;action=topic&amp;t={$contentresults[results].id}&amp;menuid={$menuid}">{$contentresults[results].subject|strip_tags:false}</a></h3>\r\n	<span class="smalltext"><b>Item Type: </b>Forum Post&nbsp;|&nbsp;<b>Relevance: </b>{$contentresults[results].score|round}</span>\r\n	<div> {$contentresults[results].numposts} {if $contentresults[results].numposts > 1}posts match{else}post matches{/if}<br /><span class="smalltext"><a href="index.php?page=forums&amp;action=topic&amp;t={$contentresults[results].id}&amp;menuid={$menuid}">Read more...</a></span></div>\r\n     \r\n     {elseif $contentresults[results].itemtype == 5}   \r\n     \r\n	<h3 style="text-align:left;margin:0px;padding:0px;"><a  href="index.php?page=news&id={$contentresults[results].id}&amp;menuid={$menuid}">{$contentresults[results].title|strip_tags:false}</a></h3>\r\n	<span class="smalltext"><b>Item Type: </b>News Item&nbsp;|&nbsp;<b>Relevance: </b>{$contentresults[results].score|round}</span>	<div>{$contentresults[results].news|strip_tags|truncate:300}<br /><span class="smalltext"><a href="index.php?page=news&id={$contentresults[results].id}&amp;menuid={$menuid}">Read more...</a></span></div>\r\n	\r\n     {elseif $contentresults[results].itemtype == 6}      \r\n     \r\n	<h3 style="text-align:left;margin:0px;padding:0px;"><a href="index.php?page=calender&item={$contentresults[results].id}&amp;menuid={$menuid}">{$contentresults[results].summary|strip_tags:false}</a></h3>\r\n	<span class="smalltext"><b>Item Type: </b>Event&nbsp;|&nbsp;<b>Relevance: </b>{$contentresults[results].score|round}</span>	\r\n	<div><b>Starts: </b>{if $contentresults[results].wholeday != 1}{$contentresults[results].startdate|date_format:"%H:%M %B %e, %Y"}{else}{$contentresults[results].startdate|date_format:"%B %e, %Y"}{/if}<br />\r\n	<b>Ends: </b>{if $contentresults[results].wholeday != 1}{$contentresults[results].enddate|date_format:"%H:%M %B %e, %Y"}{else}{$contentresults[results].enddate|date_format:"%B %e, %Y"}{/if}  <br /><span class="smalltext"><a href="index.php?page=calender&item={$contentresults[results].id}&amp;menuid={$menuid}">Read more...</a></span></div> \r\n	\r\n     {elseif $contentresults[results].itemtype == 7}       \r\n     \r\n	<h3 style="text-align:left;margin:0px;padding:0px;"><a  href="index.php?page=pmmain&action=readpm&id={$contentresults[results].id}&amp;menuid={$menuid}">{$contentresults[results].subject|strip_tags:false}</a></h3>\r\n	<span class="smalltext"><b>Item Type: </b>Personal Message&nbsp;|&nbsp;<b>Relevance: </b>{$contentresults[results].score|round}</span>	\r\n	<div>{$contentresults[results].text|strip_tags|truncate:300}<br /><span class="smalltext"><a href="index.php?page=pmmain&action=readpm&id={$contentresults[results].id}&amp;menuid={$menuid}">Read more...</a></span></div>\r\n	\r\n	     {elseif $contentresults[results].itemtype == 8}   \r\n     \r\n	<h3 style="text-align:left;margin:0px;padding:0px;"><a  href="index.php?page=subsite&amp;site={$contentresults[results].pid}&amp;content={$contentresults[results].id}&amp;menuid={$menuid}">{$contentresults[results].friendly|strip_tags:false}</a></h3>\r\n	<span class="smalltext"><b>Item Type: </b>Page for {$contentresults[results].site}&nbsp;|&nbsp;<b>Relevance: </b>{$contentresults[results].score|round}</span>\r\n		<div>{$contentresults[results].content|strip_tags|truncate:300}<br /><span class="smalltext"><a href="index.php?page=subsite&amp;site={$contentresults[results].pid}&amp;content={$contentresults[results].id}&amp;menuid={$menuid}">Read more...</a></span></div>\r\n    {/if}\r\n    </div>\r\n     <br />\r\n    {/section}\r\n    {/if}\r\n{else} \r\nYou search ({$term_list}) returned no results\r\n{/if}\r\n{/if}', 19),
