(17, 1, '<table width="100%" cellpadding="0" cellspacing="0">\r\n<tr valign="top">\r\n<td width="140">\r\n<ul class="left_title">\r\n    {if $cpallowed}<li><a href="index.php?page=usercp&amp;menuid={$menuid}">Control Panel</a></li>{/if}\r\n    {if $profileallowed}<li><a href="index.php?page=profile&amp;menuid={$menuid}">Profile</a></li>{/if}\r\n    {if $contributionallowed}<li class="active"><a href="index.php?page=mythings&amp;menuid={$menuid}">Contributions</a></li>{/if}\r\n    {if $groupsallowed}<li><a href="index.php?page=mypatrol&amp;menuid={$menuid}">Groups</a></li>{/if}\r\n    {if $pmallowed}<li><a href="index.php?page=pmmain&amp;menuid={$menuid}">Private Messages</a></li>{/if}\r\n</ul>\r\n</td>\r\n<td>\r\n{literal}\r\n<script type="text/javascript">\r\n<!--\r\nfunction confirmDelete(type, id) {\r\nif (confirm("This will delete the item. Continue?")){/literal}\r\ndocument.location = "index.php?page=mythings&action=delete&menuid={$menuid}&cat=" + type + "&id=" + id;{literal}\r\n}\r\n//-->\r\n</script>\r\n{/literal}\r\n<div align="center"><div style="width:100%">\r\n<div style="text-align:center">\r\n{if $auth.album == 1 && $pageactive.photos}<a href="index.php?page=addphotoalbum&amp;menuid={$menuid}">Add Photo Album</a> | {/if}\r\n{if $auth.article == 1 && $pageactive.patrolarticle}<a href="index.php?page=addarticle&amp;menuid={$menuid}">Add Article</a> | {/if}\r\n{if $auth.event == 1 && $pageactive.calender}<a href="index.php?page=addevent&amp;menuid={$menuid}">Add Event</a> | {/if}\r\n{if $auth.down == 1 && $pageactive.downloads}<a href="index.php?page=mythings&amp;action=adddown&amp;menuid={$menuid}">Add Download</a> | {/if}\r\n{if $auth.news == 1 && $pageactive.news}<a href="index.php?page=mythings&amp;action=addnews&amp;menuid={$menuid}">Add News</a> | {/if}\r\n{if $auth.poll == 1 && $pageactive.polls}<a href="index.php?page=addpoll&amp;menuid={$menuid}">Add Poll</a>{/if}\r\n<br />\r\n<div id="navcontainer" align="center">\r\n                        <ul class="mootabs_title">\r\n                                <!-- CSS Tabs -->\r\n{if $pageactive.photos}<li title="albums">Photo Albums</li>{/if}\r\n{if $pageactive.patrolarticle}<li title="articles">Articles</li>{/if}\r\n{if $pageactive.calender}<li title="events">Events</li>{/if}\r\n{if $pageactive.downloads}<li title="downloads">Downloads</li>{/if}\r\n{if $pageactive.news}<li title="news">News items</li>{/if}\r\n{if $pageactive.polls}<li title="polls">Polls</li>{/if}\r\n</ul>\r\n	\r\n{if $pageactive.photos}\r\n<div id="albums" class="mootabs_panel">\r\n{if $numalbums > 0}\r\n<table width = "100%" border="0" cellspacing="1" cellpadding="4" class="table">\r\n    <tr>\r\n        <th colspan="3" class="normalhead"><a name="album"></a>Photo Albums</th>\r\n    </tr>\r\n    <tr>\r\n        <th class="smallhead" width="15%">Action</th>\r\n        <th class="smallhead" width="10%">Published?</th>\r\n        <th class="smallhead">Title</th>\r\n    </tr>\r\n    {section name=album loop=$numalbums}\r\n    <tr>\r\n        <td align="center" class="row">{if $albums[album].expired != 1}<a href="index.php?page=mythings&amp;cat=album&amp;action=edit&amp;id={$albums[album].ID}&amp;menuid={$menuid}"><img border="0" src="{$templateinfo.imagedir}edit.gif" title="Edit" alt="Edit"/></a>{else}<img src="{$templateinfo.imagedir}edit_grey.gif" alt="Ownership Expired" title="Ownership Expired" border="0" />{/if}&nbsp;&nbsp;<a href="index.php?page=photos&amp;album={$albums[album].ID}&amp;menuid={$menuid}"><img border="0" src="{$templateinfo.imagedir}page.gif" title="View" alt="View"/></a>&nbsp;&nbsp;{if $albums[album].expired != 1 && $albums[album].type_owner == 0}<a href="javascript:confirmDelete(''album'',''{$albums[album].ID}'')"><img border="0" src="{$templateinfo.imagedir}delete.gif" title="Delete" alt="Delete"/></a>{else}<img src="{$templateinfo.imagedir}delete_grey.gif" border="0" alt="Not allowed" title="Not allowed" />{/if}&nbsp;&nbsp;{if $albums[album].expired != 1 && $albums[album].type_owner == 0}<a href="index.php?page=mythings&amp;cat=album&amp;action=owner&amp;id={$albums[album].ID}&amp;menuid={$menuid}"><img border="0" src="{$templateinfo.imagedir}owner.gif" title="Users/Groups with access" alt="Users/Groups with access"/></a>{else}<img src="{$templateinfo.imagedir}owner_grey.gif" alt="Not allowed" title="Not allowed" border="0" />{/if}</td>\r\n        <td align="center" class="row">{if $albums[album].allowed == 1}<img src="{$templateinfo.imagedir}published.png" alt="Published" title="Published" border="0" />{else}<img src="{$templateinfo.imagedir}notpublished.png" alt="Not Published" title="Not Published" border="0" />{/if}</td>\r\n        <td class="row">{$albums[album].album_name} {if $albums[album].expired == 1}(Ownership expired){elseif $albums[album].expire != 0}(Ownership expires: {$albums[album].expire+$timeoffset|date_format:"%B %e, %Y %H:%M"}){/if}</td>\r\n    </tr>\r\n    {/section}\r\n</table>\r\n{else}\r\n    <div style="text-align:center">You do not have ownership of any photo albums</div>\r\n{/if}\r\n</div>\r\n{/if}\r\n\r\n{if $pageactive.patrolarticle}\r\n<div id="articles" class="mootabs_panel">\r\n{if $numart > 0}\r\n<table width = "100%" border="0" cellspacing="1" cellpadding="4" class="table">\r\n    <tr>\r\n        <th colspan="3" class="normalhead"><a name="article"></a>Articles</th>\r\n    </tr>\r\n    <tr>\r\n        <th class="smallhead" width="15%">Action</th>\r\n        <th class="smallhead" width="10%">Published?</th>\r\n        <th class="smallhead">Title</th>\r\n    </tr>\r\n    {section name=album loop=$numart}\r\n    <tr>\r\n        <td align="center" class="row">{if $articles[album].expired != 1}<a href="index.php?page=mythings&amp;cat=articles&amp;action=edit&amp;id={$articles[album].ID}&amp;menuid={$menuid}"><img border="0" src="{$templateinfo.imagedir}edit.gif" title="Edit" alt="Edit"/></a>{else}<img src="{$templateinfo.imagedir}edit_grey.gif" alt="Ownership Expired" title="Ownership Expired" border="0" />{/if}&nbsp;&nbsp;<a href="index.php?page=patrolarticle&amp;id={$articles[album].ID}&amp;menuid={$menuid}"><img border="0" src="{$templateinfo.imagedir}page.gif" title="View" alt="View"/></a>&nbsp;&nbsp;{if $articles[album].expired != 1 && $articles[album].type_owner == 0}<a href="javascript:confirmDelete(''articles'',''{$articles[album].ID}'')"><img border="0" src="{$templateinfo.imagedir}delete.gif" title="Delete" alt="Delete"/></a>{else}<img src="{$templateinfo.imagedir}delete_grey.gif" border="0" alt="Not allowed" title="Not allowed" />{/if}&nbsp;&nbsp;{if $articles[album].expired != 1 && $articles[album].type_owner == 0}<a href="index.php?page=mythings&amp;cat=articles&amp;action=owner&amp;id={$articles[album].ID}&amp;menuid={$menuid}"><img border="0" src="{$templateinfo.imagedir}owner.gif" title="Users/Groups with access" alt="Users/Groups with access"/></a>{else}<img src="{$templateinfo.imagedir}owner_grey.gif" alt="Not allowed" title="Not allowed" border="0" />{/if}</td>\r\n        <td align="center" class="row">{if $articles[album].allowed == 1}<img src="{$templateinfo.imagedir}published.png" alt="Published" title="Published" border="0" />{else}<img src="{$templateinfo.imagedir}notpublished.png" alt="Not Published" title="Not Published" border="0" />{/if}</td>\r\n        <td class="row">{$articles[album].title}</td>\r\n    </tr>\r\n    {/section}\r\n</table>\r\n{else}\r\n    <div style="text-align:center">You do not have access to any articles</div>\r\n{/if}\r\n</div>\r\n{/if}\r\n\r\n{if $pageactive.calender}\r\n<div id="events" class="mootabs_panel">\r\n{if $numevents > 0}\r\n<table width = "100%" border="0" cellspacing="1" cellpadding="4" class="table">\r\n    <tr>\r\n        <th colspan="4" class="normalhead"><a name="event"></a>Events</th>\r\n    </tr>\r\n    <tr>\r\n        <th class="smallhead" width="19%">Action</th>\r\n        <th class="smallhead" width="10%">Published?</th>\r\n        <th class="smallhead">Title</th>\r\n        <th class="smallhead" width="40%">Event Date</th>\r\n    </tr>\r\n    {section name=album loop=$numevents}\r\n    <tr>\r\n        <td align="center" class="row">{if $events[album].expired != 1}<a href="index.php?page=mythings&amp;cat=events&amp;action=edit&amp;id={$events[album].id}&amp;menuid={$menuid}"><img border="0" src="{$templateinfo.imagedir}edit.gif" title="Edit" alt="Edit"/></a>{else}<img src="{$templateinfo.imagedir}edit_grey.gif" alt="Ownership Expired" title="Ownership Expired" border="0" />{/if}&nbsp;&nbsp;<a href="index.php?page=addevent&copyitem={$events[album].id}&amp;menuid={$menuid}"><img border="0" src="{$templateinfo.imagedir}page_copy.gif" title="Copy Event" alt="Copy Event"/></a>&nbsp;&nbsp;<a href="index.php?page=calender&item={$events[album].id}&amp;menuid={$menuid}"><img border="0" src="{$templateinfo.imagedir}page.gif" title="View" alt="View"/></a>&nbsp;&nbsp;{if $events[album].expired != 1 && $events[album].type_owner == 0}<a href="javascript:confirmDelete(''events'',''{$events[album].id}'')"><img border="0" src="{$templateinfo.imagedir}delete.gif" title="Delete" alt="Delete"/></a>{else}<img src="{$templateinfo.imagedir}delete_grey.gif" border="0" alt="Not allowed" title="Not allowed" />{/if}&nbsp;&nbsp;{if $events[album].expired != 1 && $events[album].type_owner == 0}<a href="index.php?page=mythings&amp;cat=events&amp;action=owner&amp;id={$events[album].id}&amp;menuid={$menuid}"><img border="0" src="{$templateinfo.imagedir}owner.gif" title="Users/Groups with access" alt="Users/Groups with access"/></a>{else}<img src="{$templateinfo.imagedir}owner_grey.gif" alt="Not allowed" title="Not allowed" border="0" />{/if}&nbsp;&nbsp;{if $events[album].expired != 1 && $events[album].signup}<a href="index.php?page=mythings&amp;cat=events&amp;action=signups&amp;id={$events[album].id}&amp;menuid={$menuid}"><img src="{$templateinfo.imagedir}checkbox.png" border="0" alt="Signup information for {$events[album].summary}" title="Signup information for {$events[album].summary}" /></a>{else}<img src="{$templateinfo.imagedir}checkbox_grey.png" border="0" alt="Signup disabled for event" title="Signup disabled for event" />{/if}</td>\r\n        <td align="center" class="row">{if $events[album].allowed == 1}<img src="{$templateinfo.imagedir}published.png" alt="Published" title="Published" border="0" />{else}<img src="{$templateinfo.imagedir}notpublished.png" alt="Not Published" title="Not Published" border="0" />{/if}</td>\r\n        <td class="row">{$events[album].summary}</td>\r\n        <td class="row">{$events[album].startdate|date_format:"%B %e, %Y"} to {$events[album].enddate|date_format:"%B %e, %Y"}</td>\r\n    </tr>\r\n    {/section}\r\n</table>\r\n{else}\r\n    <div style="text-align:center">You do not have access to any events</div>\r\n{/if}\r\n</div>\r\n{/if}\r\n\r\n{if $pageactive.downloads}\r\n<div id="downloads" class="mootabs_panel">\r\n{if $numdown > 0}\r\n<table width = "100%" border="0" cellspacing="1" cellpadding="4" class="table">\r\n    <tr>\r\n        <th colspan="4" class="normalhead"><a name="down"></a>Downloads</th>\r\n    </tr>\r\n    <tr>\r\n        <th class="smallhead" width="15%">Action</th>\r\n        <th class="smallhead" width="10%">Published?</th>\r\n        <th class="smallhead">Filename</th>\r\n        <th class="smallhead" width="40%">Title</th>\r\n    </tr>\r\n    {section name=album loop=$numdown}\r\n    <tr>\r\n        <td align="center" class="row">{if $downloads[album].expired != 1}<a href="index.php?page=mythings&amp;cat=downloads&amp;action=edit&amp;id={$downloads[album].id}&amp;menuid={$menuid}"><img border="0" src="{$templateinfo.imagedir}edit.gif" title="Edit" alt="Edit"/></a>{else}<img src="{$templateinfo.imagedir}edit_grey.gif" alt="Ownership Expired" title="Ownership Expired" border="0" />{/if}&nbsp;&nbsp;{if $downloads[album].expired != 1 && $downloads[album].type_owner == 0}<a href="javascript:confirmDelete(''downloads'',''{$downloads[album].id}'')"><img border="0" src="{$templateinfo.imagedir}delete.gif" title="Delete" alt="Delete"/></a>{else}<img src="{$templateinfo.imagedir}delete_grey.gif" border="0" alt="Not allowed" title="Not allowed" />{/if}&nbsp;&nbsp;{if $downloads[album].expired != 1 && $downloads[album].type_owner == 0}<a href="index.php?page=mythings&amp;cat=downloads&amp;action=owner&amp;id={$downloads[album].id}&amp;menuid={$menuid}"><img border="0" src="{$templateinfo.imagedir}owner.gif" title="Users/Groups with access" alt="Users/Groups with access"/></a>{else}<img src="{$templateinfo.imagedir}owner_grey.gif" alt="Not allowed" title="Not allowed" border="0" />{/if}</td>\r\n        <td align="center" class="row">{if $downloads[album].allowed == 1}<img src="{$templateinfo.imagedir}published.png" alt="Published" title="Published" border="0" />{else}<img src="{$templateinfo.imagedir}notpublished.png" alt="Not Published" title="Not Published" border="0" />{/if}</td>\r\n        <td class="row">{$downloads[album].file}</td>\r\n        <td class="row">{$downloads[album].name}</td>\r\n    </tr>\r\n    {/section}\r\n</table>\r\n{else}\r\n    <div style="text-align:center">You do not have access to any downloads</div>\r\n{/if}\r\n</div>\r\n{/if}\r\n\r\n{if $pageactive.news}\r\n<div id="news" class="mootabs_panel">\r\n{if $numnews > 0}\r\n<table width = "100%" border="0" cellspacing="1" cellpadding="4" class="table">\r\n    <tr>\r\n        <th colspan="4" class="normalhead"><a name="news"></a>News Items</th>\r\n    </tr>\r\n    <tr>\r\n        <th class="smallhead" width="15%">Action</th>\r\n        <th class="smallhead" width="10%">Published?</th>\r\n        <th class="smallhead" width="60%">Title</th>\r\n        <th class="smallhead" width="20%">Date Posted</th>\r\n    </tr>\r\n    {section name=album loop=$numnews}\r\n    <tr>\r\n        <td align="center" class="row">{if $newsitems[album].expired != 1}<a href="index.php?page=mythings&amp;cat=newsitems&amp;action=edit&amp;id={$newsitems[album].id}&amp;menuid={$menuid}"><img border="0" src="{$templateinfo.imagedir}edit.gif" title="Edit" alt="Edit"/></a>{else}<img src="{$templateinfo.imagedir}edit_grey.gif" alt="Ownership Expired" title="Ownership Expired" border="0" />{/if}&nbsp;&nbsp;<a href="index.php?page=news&id=={$newsitems[album].id}&amp;menuid={$menuid}"><img border="0" src="{$templateinfo.imagedir}page.gif" title="View" alt="View"/></a>&nbsp;&nbsp;{if $newsitems[album].expired != 1 && $newsitems[album].type_owner == 0}<a href="javascript:confirmDelete(''newsitems'',''{$newsitems[album].id}'')"><img border="0" src="{$templateinfo.imagedir}delete.gif" title="Delete" alt="Delete"/></a>{else}<img src="{$templateinfo.imagedir}delete_grey.gif" border="0" alt="Not allowed" title="Not allowed" />{/if}&nbsp;&nbsp;{if $newsitems[album].expired != 1 && $newsitems[album].type_owner == 0}<a href="index.php?page=mythings&amp;cat=newsitems&amp;action=owner&amp;id={$newsitems[album].id}&amp;menuid={$menuid}"><img border="0" src="{$templateinfo.imagedir}owner.gif" title="Users/Groups with access" alt="Users/Groups with access"/></a>{else}<img src="{$templateinfo.imagedir}owner_grey.gif" alt="Not allowed" title="Not allowed" border="0" />{/if}</td>\r\n        <td align="center" class="row">{if $newsitems[album].allowed == 1}<img src="{$templateinfo.imagedir}published.png" alt="Published" title="Published" border="0" />{else}<img src="{$templateinfo.imagedir}notpublished.png" alt="Not Published" title="Not Published" border="0" />{/if}</td>\r\n        <td class="row">{$newsitems[album].title}</td>\r\n        <td class="row">{$newsitems[album].event+$timeoffset|date_format_pretty}</td>\r\n    </tr>\r\n    {/section}\r\n</table>\r\n{else}\r\n    <div style="text-align:center">You do not have access to any news items</div>\r\n{/if}\r\n</div>\r\n{/if}\r\n\r\n{if $pageactive.polls}\r\n<div id="polls" class="mootabs_panel">\r\n{if $numpolls > 0}\r\n<table width = "100%" border="0" cellspacing="1" cellpadding="4" class="table">\r\n    <tr>\r\n        <th colspan="4" class="normalhead"><a name="polls"></a>Polls</th>\r\n    </tr>\r\n    <tr>\r\n        <th class="smallhead" width="15%">Action</th>\r\n        <th class="smallhead" width="10%">Published?</th>\r\n        <th class="smallhead" width="60%">Question</th>\r\n        <th class="smallhead" width="20%">Date Posted</th>\r\n    </tr>\r\n    {section name=album loop=$numpolls}\r\n    <tr>\r\n        <td align="center" class="row">{if $pollitems[album].expired != 1 && $pollitems[album].type_owner == 0}<a href="javascript:confirmDelete(''pollitems'',''{$pollitems[album].id}'')"><img border="0" src="{$templateinfo.imagedir}delete.gif" title="Delete" alt="Delete"/></a>{else}<img src="{$templateinfo.imagedir}delete_grey.gif" border="0" alt="Not allowed" title="Not allowed" />{/if}&nbsp;&nbsp;{if $pollitems[album].expired != 1 && $pollitems[album].type_owner == 0}<a href="index.php?page=mythings&amp;cat=pollitems&amp;action=owner&amp;id={$pollitems[album].id}&amp;menuid={$menuid}"><img border="0" src="{$templateinfo.imagedir}owner.gif" title="Users/Groups with access" alt="Users/Groups with access"/></a>{else}<img src="{$templateinfo.imagedir}owner_grey.gif" alt="Not allowed" title="Not allowed" border="0" />{/if}</td>\r\n        <td align="center" class="row">{if $pollitems[album].allowed == 1}<img src="{$templateinfo.imagedir}published.png" alt="Published" title="Published" border="0" />{else}<img src="{$templateinfo.imagedir}notpublished.png" alt="Not Published" title="Not Published" border="0" />{/if}</td>\r\n        <td class="row">{$pollitems[album].question} {if $pollitems[album].sidebox == 1}(Sidebox Poll){/if}</td>\r\n        <td class="row">{$pollitems[album].date_start+$timeoffset|date_format_pretty}</td>\r\n    </tr>\r\n    {/section}\r\n</table>\r\n{else}\r\n    <div style="text-align:center">You do not have access to any polls</div>\r\n{/if}\r\n</div>\r\n{/if}\r\n </div></div></div></td></tr></table>', 23),
(17, 2, '{literal}\r\n<script language="javascript" type="text/javascript"> \r\nfunction deletephoto(photoid, albumid) \r\n{\r\nif (confirm("This will remove the photo from the album. Continue?"))\r\ndocument.location = "index.php?page=mythings&cat=album&action=delphoto&pid=" + photoid + "&id="+ albumid;\r\n}\r\n\r\nfunction showedit(photoid, caption)\r\n{\r\n    document.getElementById(''editphoto'').style.display=''block'';\r\n    document.getElementById(''albumedit'').style.display = ''none'';\r\n    document.getElementById(''photoupload'').style.display = ''none'';\r\n    \r\n    document.getElementById(''editcaption'').value = caption;\r\n    document.getElementById(''photoid'').value = photoid;\r\n    document.getElementById(''photopreview'').innerHTML = '''';\r\n    document.getElementById(''photopreview'').innerHTML = ''<img src="thumbnail.php?pic='' + photoid + ''" alt="'' + caption + ''" />'';\r\n}\r\n\r\nfunction update()\r\n{\r\n    document.getElementById(''albumedit'').style.display = ''block'';\r\n    document.getElementById(''photoupload'').style.display = ''none'';\r\n    document.getElementById(''editphoto'').style.display=''none'';\r\n}\r\n\r\nfunction add()\r\n{\r\n    document.getElementById(''photoupload'').style.display = ''block'';\r\n    document.getElementById(''albumedit'').style.display = ''none'';\r\n    document.getElementById(''editphoto'').style.display=''none'';\r\n}\r\n</script>\r\n{/literal}\r\n<script type="text/javascript">\r\n{include file="../../scripts/validator.tpl"}\r\n</script>\r\n<h1>{$album.album_name}</h1>\r\n<div align="center"><a href="#" onclick="update();">Edit Album</a>&nbsp;|&nbsp;<a href="#" onclick="add();">Add Photo</a></div>\r\n<div id="albumedit" class="field" style="display:none;">\r\n<form method="post" name="form1" onsubmit="return checkForm([[''name'',''text'',true,0,0,'''']]);">\r\n<fieldset>\r\n<legend>Edit Album</legend>\r\n<label for="name" class="label">Name<span class="hintanchor"title="Required :: Name for the album"><img src="{$templateinfo.imagedir}help.png" alt="[?]"/></span></label>\r\n<div class="inputboxwrapper"><input id="name" name="name" type="text" size="50" maxlength="255" class="inputbox" value="{$album.album_name}"  onblur="checkElement(''name'', ''text'', true, 0, 0, '''');"/><div class="error" id="nameError"> Required: Please enter a name for the photo album.</div></div><br />\r\n\r\n<label for="group" class="label">Group<span class="hintanchor"title="Required :: Group site to place this album"><img src="{$templateinfo.imagedir}help.png" alt="[?]"/></span></label>\r\n<div class="inputboxwrapper"><select name="group" id="group" class="inputbox">\r\n               <option value="0"  {if 0 == $album.patrol}selected="selected"{/if}>None</option>\r\n                {section name=team loop=$numteams}\r\n                    <option value="{$teams[team].id}" {if $teams[team].id == $album.patrol}selected="selected"{/if}>{$teams[team].teamname}</option>\r\n                {/section}\r\n               <option value="-1" {if -1 == $album.patrol}selected="selected"{/if}>Hidden photo album</option>\r\n     </select></div><br />\r\n<div class="submitWrapper">\r\n <input type="submit" name="Submit" value="Update" class="button" />\r\n <input type="reset" name="Submit2" value="Cancel" onClick="document.getElementById(''albumedit'').style.display=''none'';" class="button" />\r\n</div>\r\n</fieldset>\r\n</form>\r\n</div>\r\n<div id="photoupload" class="field" style="display:none;">\r\n<form method="post" enctype="multipart/form-data" name="form1" onsubmit="return checkForm([[''filename'',''text'',true,0,0,'''']]);">\r\n<fieldset>\r\n<legend>Upload Photo</legend>\r\n<label for="filename" class="label">Filename<span class="hintanchor"title="Required :: Photo filename"><img src="{$templateinfo.imagedir}help.png" alt="[?]"/></span></label>\r\n<div class="inputboxwrapper"><input id="filename" name="filename" type="file" size="50" maxlength="255" class="inputbox"  onblur="checkElement(''filename'', ''text'', true, 0, 0, '''');" /><div class="error" id="filenameError"> Required: Please enter a file to upload.</div></div><br />\r\n\r\n<label for="caption" class="label">Caption<span class="hintanchor"title="Optional :: Caption for the photo"><img src="{$templateinfo.imagedir}help.png" alt="[?]"/></span></label>\r\n<div class="inputboxwrapper"><input id="caption" name="caption" type="text" size="50" maxlength="255" class="inputbox" /></div><br />\r\n<div class="submitWrapper">\r\n <input type="submit" name="Submit" value="Upload Photo" class="button" />\r\n <input type="reset" name="Submit2" value="Cancel" onClick="document.getElementById(''photoupload'').style.display=''none'';" class="button" />\r\n</div>\r\n</fieldset>\r\n</form>\r\n</div>\r\n<div class="field" id="editphoto"  style="display:none;">\r\n<form  method="post" enctype="multipart/form-data" name="form1">\r\n<fieldset>\r\n<legend>Edit Photo</legend>\r\n<div id="photopreview" align="center"></div>\r\n<label for="editfilename" class="label">New File<span class="hintanchor"title="Optional :: Photo filename. Leave blank if you wish to keep the photo the same."><img src="{$templateinfo.imagedir}help.png" alt="[?]"/></span></label>\r\n<div class="inputboxwrapper"><input id="editfilename" name="editfilename" type="file" size="50" maxlength="255" class="inputbox" /></div><br />\r\n\r\n<label for="editcaption" class="label">Caption<span class="hintanchor"title="Optional :: Caption for the photo"><img src="{$templateinfo.imagedir}help.png" alt="[?]"/></span></label>\r\n<div class="inputboxwrapper"><input id="editcaption" name="editcaption" type="text" size="50" maxlength="255" class="inputbox" /></div><br />\r\n<input name="photoid" id="photoid" type="hidden" />\r\n<div class="submitWrapper">\r\n <input type="submit" name="Submit" value="Update Photo" class="button" />\r\n <input type="reset" name="Submit2" value="Cancel" onClick="document.getElementById(''editphoto{$photos[photo].ID}'').style.display=''none'';" class="button" />\r\n</div>\r\n</fieldset>\r\n</form>\r\n</div>\r\n{if $numphotos > 0}\r\n<table width="100%" border="0" cellspacing="1" cellpadding="4" class="table">\r\n    <tr>\r\n        <th width="5%" class="smallhead"></th>\r\n        <th width="10%" class="smallhead">Published?</th>\r\n        <th width="140px" class="smallhead">Photo Preview</th>\r\n        <th class="smallhead">Photo Caption</th>\r\n    </tr>\r\n    {section name=photo loop=$numphotos}\r\n    <tr valign="top">\r\n        <td align="center" class="row"><a href="#" onclick="showedit({$photos[photo].ID}, ''{$photos[photo].caption}'');" style="text-align:center"><img border="0" src="{$templateinfo.imagedir}edit.gif" title="Edit" alt="Edit"/></a>&nbsp;&nbsp;<a href="javascript:deletephoto({$photos[photo].ID},{$album.ID})" style="text-align:center"><img border="0" src="{$templateinfo.imagedir}delete.gif" title="Delete" alt="Delete"/></a></td>\r\n        <td align="center" class="row">{if $photos[photo].allowed == 1}<img src="{$templateinfo.imagedir}published.png" alt="Published" title="Published" border="0" />{else}<img src="{$templateinfo.imagedir}unpublished.png" alt="Not Published" title="Not Published" border="0" />{/if}</td>\r\n        <td class="row" align="center"><a href="getphoto.php?pic={$photos[photo].ID}" rel="lightbox" title="{$photos[photo].caption}" border="0"><img border="0" src="thumbnail.php?pic={$photos[photo].ID}" /></a></td>\r\n        <td class="row">{$photos[photo].caption}</td>\r\n    </tr>\r\n    {/section}\r\n</table>\r\n{else}\r\n<div align="center">No photos in album</div>\r\n{/if}\r\n<br /><a href="index.php?page=mythings&amp;menuid={$menuid}">Back</a>', 24),
(17, 5, '{literal}\r\n<script type="text/javascript">\r\n<!--\r\nfunction confirmDelete(articleId) {\r\nif (confirm("This will remove this owner. Continue?"))\r\n{/literal}\r\ndocument.location = "index.php?page=mythings&action=deleteowner&id=" + articleId + "&menuid={$menuid}&cat={$cat}&itemid={$itemid}";\r\n}\r\n//-->\r\n</script>\r\n<h2>Owners</h2>\r\n<table width="100%" border="0" cellspacing="1" cellpadding="4" align="center" class="table">\r\n<tr> \r\n   <th width="5%" class="smallhead">Action</th>\r\n   <th width="15%" class="smallhead">Expires</th>\r\n  <th class="smallhead" colspan="2">Owners</th>\r\n</tr>\r\n{section name=owners loop=$numitemowners}\r\n<tr>\r\n<td class="row"><div align="center">{if $numitemowners > 1}<a href="javascript:confirmDelete(''{$itemowners[owners].id}'')" title="Remove {$itemowners[owners].name}"><img src="{$templateinfo.imagedir}delete.gif"  border="0" alt="Remove {$itemowners[owners].name}" /></a>{else}<img src="{$templateinfo.imagedir}delete_grey.gif"  border="0" alt="Can''t remove only owner" />{/if}</div></td>\r\n<td class="row" {if $itemowners[owners].type == 0 && $itemowners[owners].expired == 0}style="background-color:#eeffff;"{elseif $itemowners[owners].expired == 1}style="background-color:#ff0000;color:#ffffff;"{/if}>{if $itemowners[owners].expire == 0}Forever{else}{$itemowners[owners].expire+$timeoffset|date_format:"%B %e, %Y %H:%M"}{/if}</td>\r\n<td class="row" {if $itemowners[owners].type == 0 && $itemowners[owners].expired == 0}style="background-color:#eeffff;"{elseif $itemowners[owners].expired == 1}style="background-color:#ff0000;color:#ffffff;"{/if} colspan="2">{$itemowners[owners].name}</td>\r\n</tr>\r\n{/section}\r\n<tr>\r\n<th class="smallhead" colspan="4">Colour Key</td>\r\n</tr>\r\n<tr>\r\n<td class="row" colspan="2">Edit Only</td>\r\n<td class="row" width="43%" style="background-color:#eeffff;">Complete Ownership</td>\r\n<td class="row" width="43%" style="background-color:#ff0000;color:#ffffff;">Expired</td>\r\n</tr>\r\n<tr>\r\n<th class="smallhead" colspan="4">Add user/group</td>\r\n</tr>\r\n<tr>\r\n<td class="row" colspan="4">\r\n<form method="post" action="{$editFormAction}">\r\n<select name="owner" id="owner" class="inputbox">\r\n<option value="0">Select user/group to add</option>\r\n    {section name="loop" loop=$numpeople}\r\n        <option value="user_{$people[loop].id}">User: {$people[loop].uname}</option>\r\n    {/section}\r\n    {section name="loop" loop=$numteams}\r\n        <option value="group_{$groups[loop].id}">Group: {$groups[loop].teamname}</option>\r\n    {/section}\r\n</select> <br />\r\nType of ownership: <select name="type_owner" id="type_owner" class="inputbox">\r\n<option value="0">Complete (Allowed to edit, delete, modify owners)</option>\r\n<option value="1">Edit only</option>\r\n</select><br />\r\nLength of time for ownership: <select name="expire" id="expire" class="inputbox">\r\n<option value="0">Forever</option>\r\n<option value="24">24 Hours</option>\r\n<option value="48">48 Hours</option>\r\n<option value="168">7 Days</option>\r\n<option value="672">4 Weeks</option>\r\n</select><br />\r\n<input type="submit" class="button" value="Add" name="action" id="action" />&nbsp;<input type="button" class="button" value="Back" onclick="window.location=''index.php?page=mythings&menuid={$menuid}''"/>\r\n</form></td>\r\n</tr>\r\n</table>', 27);
#@#INSERT INTO `!#prefix#!pagecontent` (`pageid`, `pagenum`, `content`, `id`) VALUES 
(17, 6, '<script type="text/javascript">\r\n{include file="../../scripts/validator.tpl"}\r\n</script>\r\n{if $preview}\r\n<div class="article">\r\n<h2 class="articletitle">{$post.title} (Preview)</h2>\r\n<div class="articleinfo"><b>Date Posted: </b>{$post.datepost+$timeoffset|date_format_pretty}<br />\r\n{if $post.event != 0}<b>Event: </b><a href="index.php?page=calender&item={$previewevent.id}&menuid={$menuid}">{$previewevent.summary}</a> ({$previewevent.startdate+$timeoffset|date_format:"%Y-%m-%d"} to {$previewevent.enddate+$timeoffset|date_format:"%Y-%m-%d"})<br />{/if}\r\n<b>Author: </b>{$post.auth}<br />\r\n{if $post.relatedlist}<b>Related Articles: </b>{$post.relatedlist}<br />{/if}\r\nBack to topic\r\n</div>\r\n\r\n<div class="articledetail">\r\n{if $post.pic}<div class="articlepicture"><img src="getphoto.php?pic={$post.pic}&amp;where=article&amp;maxsizex={$config.photox}&amp;maxsizey={$config.photoy}" /></div>{/if}\r\n{$post.story}\r\n</div>\r\n\r\n<div class="articlebottom">\r\n<div class="articlephotos">\r\n{if $post.photo && $number_of_photos > 0} \r\n<h3>Photo''s for article</h3>\r\n<div align="center">\r\n<div id="myGallery" style="text-align:center; width: {$config.photox+50}px; height: {$config.photoy+50}px;">\r\n{section name=pics loop=$number_of_photos}\r\n<div class="imageElement">\r\n<h3>{$previewphoto[pics].caption}</h3>\r\n<p><strong>Date Posted: {$previewphoto[pics].date+$timeoffset|date_format_pretty}</strong></p>\r\n<a href="index.php?page=patrolarticle&amp;album={$previewphoto[pics].album_id}" title="Open Album" class="open"></a>\r\n<img src="getphoto.php?pic={$previewphoto[pics].ID}&amp;maxsizex={$config.photox}&amp;maxsizey={$config.photoy}" alt="Image" class="full" />\r\n<img src="thumbnail.php?pic={$previewphoto[pics].ID}" class="thumbnail" alt="thumbnail"/>\r\n</div>\r\n{/section}\r\n</div></div>\r\n{/if}\r\n</div>\r\n\r\n<div class="articlecomments">\r\n<h3>Comments</h3>\r\n                <div class="comments">\r\n                     <div class="commentname"><span style="font-weight:bold;">Lorem ipsum</span> ({$post.datepost+$timeoffset|date_format_pretty})</div>\r\n                     <div class="commentself">Lorem ipsum dolor sit amet, consectetuer adipiscing elit. Ut convallis, sapien a bibendum imperdiet, leo leo tincidunt nunc, eu consequat urna magna vitae eros. Nulla facilisi. Suspendisse potenti. Donec eleifend malesuada justo. Morbi congue lorem et est. Vestibulum ante ipsum.</div>\r\n                </div>\r\n                <div class="comments">\r\n                     <div class="commentname"><span style="font-weight:bold;">Lorem ipsum</span> ({$post.datepost+$timeoffset|date_format_pretty})</div>\r\n                     <div class="commentself">Lorem ipsum dolor sit amet, consectetuer adipiscing elit. Ut convallis, sapien a bibendum imperdiet, leo leo tincidunt nunc, eu consequat urna magna vitae eros. Nulla facilisi. Suspendisse potenti. Donec eleifend malesuada justo. Morbi congue lorem et est. Vestibulum ante ipsum.</div>\r\n                </div>\r\n                <div class="comments">\r\n                     <div class="commentname"><span style="font-weight:bold;">Lorem ipsum</span> ({$post.datepost+$timeoffset|date_format_pretty})</div>\r\n                     <div class="commentself">Lorem ipsum dolor sit amet, consectetuer adipiscing elit. Ut convallis, sapien a bibendum imperdiet, leo leo tincidunt nunc, eu consequat urna magna vitae eros. Nulla facilisi. Suspendisse potenti. Donec eleifend malesuada justo. Morbi congue lorem et est. Vestibulum ante ipsum.</div>\r\n                </div>\r\n                <div class="comments">\r\n                     <div class="commentname"><span style="font-weight:bold;">Lorem ipsum</span> ({$post.datepost+$timeoffset|date_format_pretty})</div>\r\n                     <div class="commentself">Lorem ipsum dolor sit amet, consectetuer adipiscing elit. Ut convallis, sapien a bibendum imperdiet, leo leo tincidunt nunc, eu consequat urna magna vitae eros. Nulla facilisi. Suspendisse potenti. Donec eleifend malesuada justo. Morbi congue lorem et est. Vestibulum ante ipsum.</div>\r\n                </div>\r\n\r\n                    <div class="field">\r\n                     <label for="comment" class="label">Post your comment</label>\r\n                     <div class="inputboxwrapper"><textarea name="comment" id="comment" rows="10" class="inputbox" disabled="disabled"></textarea></div><br /> \r\n                     </div>\r\n                     <div class="submitWrapper">\r\n                     <input type="submit" name="submit" id="submit" value="Post Comment" class="button"  disabled="disabled"/>&nbsp;<input type="reset" value="Cancel Comment" id="reset" class="button"  disabled="disabled"/>\r\n                     </div>\r\n        </div>\r\n        <div style="clear:both;">&nbsp;</div>\r\n</div>\r\n</div>\r\n{/if}\r\n<form method="post" name="noteadd" id="noteadd" enctype="multipart/form-data" onsubmit="return checkForm([[''title'',''text'',true,0,0,''''], [''auth'',''text'',true,0,50,''''], [''order'', ''number'', true, 0, 0, '''']]);">\r\n<fieldset>\r\n<legend>Edit Article</legend>\r\n<div class="field">\r\n      <label for="title" class="label">Title<span class="hintanchor"title="Required :: Title for the article"><img src="{$templateinfo.imagedir}help.png" alt="[?]"/></span></label>\r\n        <div class="inputboxwrapper"><input name="title" type="text" id="title" size="50" value="{$post.title}" class="inputbox" onblur="checkElement(''title'', ''text'', true, 0, 0, '''');" />\r\n        <div class="error" id="titleError">Required: Please enter a title for the article.</div></div><br />\r\n    \r\n      <label for="auth" class="label">Author<span class="hintanchor"title="Required :: The author of this article"><img src="{$templateinfo.imagedir}help.png" alt="[?]"/></span></label>\r\n        <div class="inputboxwrapper"><input name="auth" type="text" id="auth" size="50" value="{if $post.auth}{$post.auth}{else}{$name}{/if}" class="inputbox" onblur="checkElement(''auth'', ''text'', true, 0, 50, '''');" />\r\n        <div class="error" id="authError">Required: Please enter a author name that is shorter then 50 characters.</div></div><br />\r\n   \r\n    {if $numalbum > 0}\r\n    {literal}\r\n<script type="text/javascript">\r\nfunction selectImage(id)\r\n{\r\n   oldid = document.getElementById(''articlephoto'').value;\r\n   document.getElementById(''articlephoto'').value = id;\r\n   \r\n    if (document.getElementById(oldid))\r\n    {    \r\n        document.getElementById(oldid).style.borderWidth = "0px";\r\n    }\r\n    document.getElementById(id).style.borderWidth = "5px";\r\n}\r\n\r\nfunction getAlbumData()\r\n{\r\n    var index = document.getElementById(''albumSelect'').selectedIndex;\r\n    var id = document.getElementById(''albumSelect'').options[index].value;\r\n    var albums = new Array();\r\n    var photoId = new Array();\r\n    {/literal}\r\n    {section name=album loop=$numalbum}\r\n       {if $albums[album].numphotos}\r\n       albums[{$albums[album].ID}] = "<table width="100%" cellspacing="0" cellpadding="2" align="center"><tr style="height:135px">{section name=photos loop=$albums[album].numphotos}<td width="135px" style="vertical-align:middle;"><div align="center" ><a href="javascript:selectImage(''{$albums[album].photos[photos].ID}'');" title="Insert Photo"><img border="0" src="thumbnail.php?pic={$albums[album].photos[photos].ID}" alt="Insert Photo" class="selectImage" id="{$albums[album].photos[photos].ID}" /></a></div></td>{if ($smarty.section.photos.iteration % 3 == 0)}</tr><tr style="height:135px">{/if}{/section}</tr></table>";{/if}\r\n    {/section}\r\n    {literal}\r\n    if (id != 0)\r\n    {\r\n        description_div = document.getElementById(''showAlbum'');\r\n        description_div.innerHTML = '''';\r\n        description_div.innerHTML = albums[id];\r\n        description_div.style.height = "280px";\r\n        selectedID = document.getElementById(''articlephoto'').value; \r\n        if (document.getElementById(selectedID))\r\n        {\r\n            document.getElementById(selectedID).style.borderWidth = "5px";\r\n        }\r\n    }\r\n    else\r\n    {\r\n        document.getElementById(''articlephoto'').value = 0;\r\n        description_div = document.getElementById(''showAlbum'');        \r\n        description_div.innerHTML = '''';\r\n        description_div.innerHTML = ''No album selected'';\r\n        description_div.style.height = "";\r\n    }\r\n}\r\n</script>\r\n{/literal}\r\n     <label for="photo" class="label">Photo Album<span class="hintanchor"title="Optional :: Attach a photo album to this article."><img src="{$templateinfo.imagedir}help.png" alt="[?]"/></span></label>\r\n        <div class="inputboxwrapper"><select name="photo" id="photo" class="inputbox" style="width:80%">\r\n            <option value="0" selected>None</option>\r\n          {section name=albums loop=$numalbum}\r\n            {if $albums[albums].numphotos > 0}\r\n            <option value="{$albums[albums].ID}" {if $post.album_id == $albums[albums].ID}selected="selected"{/if}>{$albums[albums].album_name}</option>\r\n            {/if}\r\n          {/section}\r\n        </select> <a href="index.php?page=addphotoalbum&menuid={$menuid}" target="_blank">Add album</a></div><br />\r\n        \r\n     <label for="albumSelect" class="label">Article Photo<span class="hintanchor"title="Optional :: This picture will be shown at the top of the article when an user views it."><img src="{$templateinfo.imagedir}help.png" alt="[?]"/></span></label>\r\n        <div class="inputboxwrapper"><select id="albumSelect" class="inputbox" style="width:80%" onchange="getAlbumData();">\r\n            <option value="0" selected>None</option>\r\n          {section name=albums loop=$numalbum}\r\n            {if $albums[albums].numphotos > 0}\r\n            <option value="{$albums[albums].ID}" {if $selectedAlbum == $albums[albums].ID}selected="selected"{/if}>{$albums[albums].album_name}</option>\r\n            {/if}\r\n          {/section}\r\n        </select><br /><div id="showAlbum" style="overflow:auto;width:480px;{if $selectedAlbum}height:280px{/if}">{if !$selectedAlbum}No album selected{else}<table width="100%" cellspacing="0" cellpadding="2" align="center"><tr style="height:135px">{section name=photos loop=$selectedAlbumInfo.numphotos}<td width="135px" style="vertical-align:middle;"><div align="center" ><a href="javascript:selectImage(''{$selectedAlbumInfo.photos[photos].ID}'');" title="Insert Photo"><img border="0" src="thumbnail.php?pic={$selectedAlbumInfo.photos[photos].ID}" alt="Insert Photo" class="selectImage" id="{$selectedAlbumInfo.photos[photos].ID}" {if $post.pic == $selectedAlbumInfo.photos[photos].ID}style="border-width:5px;"{/if}/></a></div></td>{if ($smarty.section.photos.iteration % 3 == 0)}</tr><tr style="height:135px">{/if}{/section}</tr></table>{/if}</div><input type="hidden" name="articlephoto" id="articlephoto" value="{$post.pic}" /></div><br />\r\n   {/if}\r\n   \r\n  {if $numevents > 0}\r\n     <label for="event" class="label">Event<span class="hintanchor"title="Optional :: Attach a event to this article."><img src="{$templateinfo.imagedir}help.png" alt="[?]"/></span></label>\r\n        <div class="inputboxwrapper"><select name="event" id="event" class="inputbox" style="width:80%">\r\n            <option value="0" selected>None</option>\r\n          {section name=events loop=$numevents}\r\n            <option value="{$event[events].id}" {if $post.event_id == $event[events].id}selected="selected"{/if}>{$event[events].summary} ({$event[events].startdate|date_format:"%Y-%m-%d"} to {$event[events].enddate|date_format:"%Y-%m-%d"})</option>\r\n          {/section}\r\n        </select> <a href="index.php?page=addevent&menuid={$menuid}" target="_blank">Add event</a></div><br />\r\n  {/if} \r\n    \r\n         <label for="patrol" class="label">Group<span class="hintanchor"title="Required :: Group site to place this article"><img src="{$templateinfo.imagedir}help.png" alt="[?]"/></span></label>\r\n        <div class="inputboxwrapper"><select name="patrol" id="patrol" class="inputbox">\r\n            <option value="0" {if $post.patrol == 0}selected="selected"{/if}>None</option>\r\n            {section name=team loop=$numteams}\r\n                <option value="{$teams[team].id}" {if $post.patrol == $teams[team].id}selected="selected"{/if}>{$teams[team].teamname}</option>\r\n            {/section}\r\n        </select></div><br />\r\n                \r\n      <span class="label">Topics<span class="hintanchor"title="Optional :: Article topics"><img src="{$templateinfo.imagedir}help.png" alt="[?]"/></span></span>\r\n      <div class="inputboxwrapper"><ul class="checklist" style="height:10em;">\r\n{section name=topic loop=$numtopics}\r\n{assign var="id" value=$topics[topic].id}\r\n   <li><label for="topics{$topics[topic].id}"><input type="checkbox" value="1" name="topics[{$topics[topic].id}]" id="topics{$topics[topic].id}"  {if $post.topics.$id == 1}checked="checked"{/if} />{$topics[topic].title}</label></li>\r\n   {sectionelse}\r\n   <li>No topics</li>\r\n{/section} \r\n</ul></div><br />\r\n\r\n      <span class="label">Related Articles<span class="hintanchor"title="Optional :: Related articles"><img src="{$templateinfo.imagedir}help.png" alt="[?]"/></span></span>\r\n      <div class="inputboxwrapper"><ul class="checklist" style="height:10em;">\r\n{section name=articles loop=$numarticles}\r\n{assign var="id" value=$article[articles].ID}\r\n   <li><label for="articles{$article[articles].ID}"><input type="checkbox" value="1" name="articles[{$article[articles].ID}]" id="articles{$article[articles].ID}"  {if $post.related.$id == 1}checked="checked"{/if} />{$article[articles].title}</label></li>\r\n      {sectionelse}\r\n   <li>No available articles</li>\r\n{/section} \r\n</ul></div><br />\r\n          \r\n<label for="order" class="label">Position<s