(17, 8, '  <script type="text/javascript">\r\n   {literal}\r\n   function waitforupload()\r\n   {\r\n        downloadwindow = document.getElementById(''downloadwindow'');\r\n        waitwindow = document.getElementById(''waitwindow'');\r\n\r\n        waitwindow.style.display = "block";\r\n        downloadwindow.style.display = "none";\r\n        return true;\r\n   }\r\n   {/literal}\r\n   {include file="../../scripts/validator.tpl"}\r\n  </script>\r\n  <div id="downloadwindow" style="display: '''';">\r\n  <form method="post" enctype="multipart/form-data"  name="form1" onsubmit="waitforupload();return checkForm([[''name'',''text'',true,0,0,''''],[''desc'', ''text'', true, 0, 0, '''']{if $action!="edit"},[''file'',''text'',true,0,0,'''']{/if}]);">\r\n  <fieldset>\r\n  <legend>{if $action=="edit"}Edit{else}Add{/if} download</legend>\r\n  <div class="field">\r\n    <label for="name" class="label">Name<span class="hintanchor"title="Required :: Name for the download"><img src="{$templateinfo.imagedir}help.png" alt="[?]"/></span></label>\r\n<div class="inputboxwrapper"><input name="name" type="text" id="name" maxlength="50" value="{$down.name}" class="inputbox" onblur="checkElement(''name'', ''text'', true, 0, 0, '''');" /><br /><div class="error" id="nameError"> Required: Please enter a name for the download.</div></div><br /> \r\n\r\n\r\n      <label for="desc" class="label">Description<span class="hintanchor"title="Required :: A short description of the download"><img src="{$templateinfo.imagedir}help.png" alt="[?]"/></span></label>\r\n<div class="inputboxwrapper"><textarea name="desc" rows="5" id="desc" class="inputbox" onblur="checkElement(''desc'', ''text'', true, 0, 0, '''');">{$down.descs}</textarea><br /><div class="error" id="descError"> Required: Please enter a description for the download.</div></div><br /> \r\n    {if $numalbum > 0}\r\n    {literal}\r\n<script type="text/javascript">\r\nfunction selectImage(id)\r\n{\r\n   oldid = document.getElementById(''downloadphoto'').value;\r\n   document.getElementById(''downloadphoto'').value = id;\r\n   \r\n    if (document.getElementById(oldid))\r\n    {    \r\n        document.getElementById(oldid).style.borderWidth = "0px";\r\n    }\r\n    document.getElementById(id).style.borderWidth = "5px";\r\n}\r\n\r\nfunction getAlbumData()\r\n{\r\n    var index = document.getElementById(''albumSelect'').selectedIndex;\r\n    var id = document.getElementById(''albumSelect'').options[index].value;\r\n    var albums = new Array();\r\n    var photoId = new Array();\r\n    {/literal}\r\n    {section name=album loop=$numalbum}\r\n       {if $albums[album].numphotos}\r\n       albums[{$albums[album].ID}] = "<table width="100%" cellspacing="0" cellpadding="2" align="center"><tr style="height:135px">{section name=photos loop=$albums[album].numphotos}<td width="135px" style="vertical-align:middle;"><div align="center" ><a href="javascript:selectImage(''{$albums[album].photos[photos].ID}'');" title="Insert Photo"><img border="0" src="thumbnail.php?pic={$albums[album].photos[photos].ID}" alt="Insert Photo" class="selectImage" id="{$albums[album].photos[photos].ID}" /></a></div></td>{if ($smarty.section.photos.iteration % 3 == 0)}</tr><tr style="height:135px">{/if}{/section}</tr></table>";{/if}\r\n    {/section}\r\n    {literal}\r\n    if (id != 0)\r\n    {\r\n        description_div = document.getElementById(''showAlbum'');\r\n        description_div.innerHTML = '''';\r\n        description_div.innerHTML = albums[id];\r\n        description_div.style.height = "280px";\r\n        selectedID = document.getElementById(''downloadphoto'').value; \r\n        if (document.getElementById(selectedID))\r\n        {\r\n            document.getElementById(selectedID).style.borderWidth = "5px";\r\n        }\r\n    }\r\n    else\r\n    {\r\n        document.getElementById(''downloadphoto'').value = 0;\r\n        description_div = document.getElementById(''showAlbum'');        \r\n        description_div.innerHTML = '''';\r\n        description_div.innerHTML = ''No album selected'';\r\n        description_div.style.height = "";\r\n    }\r\n}\r\n</script>\r\n{/literal}\r\n     <label for="albumSelect" class="label">Thumbnail Photo<span class="hintanchor"title="Optional :: Select an image to use as a thumbnail image for this download."><img src="{$templateinfo.imagedir}help.png" alt="[?]"/></span></label>\r\n        <div class="inputboxwrapper"><select id="albumSelect" class="inputbox" style="width:80%" onchange="getAlbumData();">\r\n            <option value="0" selected>None</option>\r\n          {section name=albums loop=$numalbum}\r\n            {if $albums[albums].numphotos > 0}\r\n            <option value="{$albums[albums].ID}" {if $selectedAlbum == $albums[albums].ID}selected="selected"{/if} title="{$albums[albums].album_name}">{$albums[albums].album_name|truncate:30} (Contains {$albums[albums].numphotos} photos)</option>\r\n            {/if}\r\n          {/section}\r\n        </select><br /><div id="showAlbum" style="overflow:auto;width:480px;{if $selectedAlbum}height:280px{/if}">{if !$selectedAlbum}No album selected{else}<table width="100%" cellspacing="0" cellpadding="2" align="center"><tr style="height:135px">{section name=photos loop=$selectedAlbumInfo.numphotos}<td width="135px" style="vertical-align:middle;"><div align="center" ><a href="javascript:selectImage(''{$selectedAlbumInfo.photos[photos].ID}'');" title="Insert Photo"><img border="0" src="thumbnail.php?pic={$selectedAlbumInfo.photos[photos].ID}" alt="Insert Photo" class="selectImage" id="{$selectedAlbumInfo.photos[photos].ID}" {if $down.thumbnail == $selectedAlbumInfo.photos[photos].ID}style="border-width:5px;"{/if}/></a></div></td>{if ($smarty.section.photos.iteration % 3 == 0)}</tr><tr style="height:135px">{/if}{/section}</tr></table>{/if}</div><input type="hidden" name="downloadphoto" id="downloadphoto" value="{$down.thumbnail}" /></div><br />\r\n   {/if}\r\n\r\n      {if $down.file}<span class="label">Current File</span><div class="inputboxwrapper">{$down.file}</div><br />{/if}\r\n    <label for="file" class="label">Filename<span class="hintanchor"title="Required :: File that you wish to upload<br />{if $action=="edit"}Leave blank if you don''t want to change the file<br />{/if}Maximum size: {$config.uploadlimit}Kb"><img src="{$templateinfo.imagedir}help.png" alt="[?]"/></span></label>\r\n<div class="inputboxwrapper"><input name="file" id="file" type="file" size="60" style="width:90%" class="inputbox" {if $action!="edit"}onblur="checkElement(''file'', ''text'', true, 0, 0, '''');"{/if} />{if !$action=="edit"}<br /><div class="error" id="fileError"> Required: Please enter a filename to upload.</div>{/if}</div><br />\r\n\r\n    <label for="cat" class="label">Category<span class="hintanchor"title="Required :: Category for the download"><img src="{$templateinfo.imagedir}help.png" alt="[?]"/></span></label>\r\n<div class="inputboxwrapper"><select name="cat" id="cat" class="inputbox">\r\n        {section name=cats loop=$numcats}\r\n            <option value="{$cat[cats].id}"{if $cat[cats].id == $down.cat} selected{/if}>{$cat[cats].name}</option>\r\n        {/section}\r\n    </select></div><br />\r\n    </div>\r\n    <div class="submitWrapper">\r\n    <input name="validation" type="hidden" id="validation" value="name,text,true,0,0,0" />\r\n    <input type="submit" name="Submit" value="Submit" class="button" />\r\n    <input type="reset" name="Submit2" value="Cancel" class="button" onClick="history.go(-1);" />\r\n    </div>\r\n    </fieldset>\r\n</form>\r\n</div>\r\n<div id="waitwindow" style="display: none;">\r\n<img src="{$templateinfo.imagedir}wait.gif" alt="Wait..." /> Please wait while uploading, this may take a few minutes depending on the size of the file and your connection speed....\r\n</div>', 34),
(17, 9, '<script type="text/javascript">\r\n{include file="../../scripts/validator.tpl"}\r\n</script>\r\n<form name="News" method="post" onsubmit="return checkForm([[''title'',''text'',true,0,100,'''']]);">\r\n<fieldset>\r\n<legend>{if $action=="edit"}Edit{else}Add{/if} News Item</legend>\r\n<div class="field">\r\n<label for="title" class="label">Title<span class="hintanchor"title="Required :: Title for news item"><img src="{$templateinfo.imagedir}help.png" alt="[?]"/></span></label>\r\n<div class="inputboxwrapper"><input type="text" name="title" id="title" size="60" maxlength="100" value="{$shownews.title}" class="inputbox" onblur="checkElement(''title'', ''text'', true, 0, 100, '''');"/><div class="error" id="titleError">Required: Please enter a title for the news item.</div></div><br />\r\n\r\n<label for="attachment" class="label">Related Item<span class="hintanchor" title="Generaly this will be the item that the news is about, or related too."><img src="{$templateinfo.imagedir}help.png" alt="[?]"/></span></label>\r\n<div class="inputboxwrapper"><select name="attachment" id="attachment" class="inputbox">\r\n  <option value="0" {if $shownews.attachment == 0}selected="selected"{/if}>None</option>\r\n        <optgroup label="Articles">\r\n            {section name=thing loop=$numarticles}\r\n                <option value="{$article[thing].ID}.article" {if $shownews.attachment == $article[thing].idType}selected="selected"{/if}>{$article[thing].title}</option>\r\n            {/section}\r\n            </optgroup>\r\n            <optgroup label="Photo Albums">\r\n            {section name=thing loop=$numalbums}\r\n                <option value="{$album[thing].ID}.album" {if $shownews.attachment == $album[thing].idType}selected="selected"{/if}>{$album[thing].album_name}</option>\r\n            {/section}\r\n            </optgroup>\r\n            <optgroup label="Events">\r\n            {section name=thing loop=$numevents}\r\n                <option value="{$event[thing].id}.event" {if $shownews.attachment == $event[thing].idType}selected="selected"{/if}>{$event[thing].summary}</option>\r\n            {/section}\r\n            </optgroup>\r\n            <optgroup label="Downloads">\r\n            {section name=thing loop=$numdownloads}\r\n                <option value="{$download[thing].id}.download" {if $shownews.attachment == $download[thing].idType}selected="selected"{/if}>{$download[thing].name}</option>\r\n            {/section}\r\n            </optgroup>\r\n            <optgroup label="News">\r\n            {section name=thing loop=$numnews}\r\n                <option value="{$news[thing].id}.news" {if $shownews.attachment == $news[thing].idType}selected="selected"{/if}>{$news[thing].title}</option>\r\n            {/section}\r\n            </optgroup>\r\n        </select>\r\n        </div><br />\r\n</div>\r\n\r\n<textarea id="story" name="story" style="width: 100%; height: 25em;">{$shownews.news}</textarea>\r\n<div class="submitWrapper">\r\n<input name="validation" type="hidden" id="validation" value="title,text,true,0,100,0;story,text,true,0,0,0" />\r\n<input type="submit" name="submit" value="Submit" class="button" /> \r\n<input type="button" name="Submit2" value="Cancel" onClick="window.location=''index.php?page=mythings''" class="button" />\r\n</div>\r\n</form>', 35),
(21, 1, '{if $numpoints}\r\n<table width="50%" cellpadding="4" cellspacing="1" border="0" class="table" align="center">\r\n<tr>\r\n<th class="normalhead" colspan="3">Group Points</th>\r\n</tr>\r\n<tr>\r\n<th class="smallhead" width="10%">Place</th>\r\n<th class="smallhead">Group</th>\r\n<th class="smallhead" width="10%">Points</th>\r\n</tr>\r\n{section name=points loop=$numpoints}\r\n<tr>\r\n<td align="right" class="row">{$smarty.section.points.iteration}</td>\r\n<td align="center" class="row">{if $patrolpoints[points].teamname==$userpatrol}<h3>{$patrolpoints[points].teamname}</h3>{else}{$patrolpoints[points].teamname}{/if}</td>\r\n<td align="center" class="row">{$patrolpoints[points].points}</td>\r\n</tr>\r\n{/section}\r\n</table>\r\n{else}\r\nThere are no groups who are receiving points.\r\n{/if}', 36),
(22, 1, '<script type="text/javascript">\r\n<!--\r\n{include file="../../scripts/validator.tpl"}\r\n-->\r\n</script>\r\n    {literal}\r\n<script type="text/javascript">\r\n<!--\r\n    function checkAll(type) \r\n{\r\n    switch (type)\r\n    {\r\n        case ''group'':\r\n            {/literal}itemList = [''0''{section name=team loop=$numteams},''{$teams[team].id}''{/section}];\r\n            number = {$numteams+1};{literal}\r\n            break;\r\n        {/literal}{if $numpatrols}\r\n        case ''patrol'':\r\n            itemList = [{section name=patrols loop=$numpatrols}''{$patrols[patrols].id}''{if $smarty.section.patrols.iteration <$numpatrols},{/if}{/section}];\r\n            number = {$numpatrols};\r\n            break;\r\n        {/if}{literal}\r\n    }\r\n    for (i=0;i<number;i++)\r\n    {\r\n        document.getElementById(type + itemList[i]).checked = document.getElementById(type+''_all'').checked;\r\n    }\r\n}\r\n//-->\r\n</script>\r\n{/literal}\r\n	<form method="post" name="form1" id="form1" onsubmit="return checkForm([[''summary'',''text'',true,0,50,''''], [''sdate'',''date'',true,0,0,''''], [''edate'',''date'',true,0,0,'''']]);">\r\n    <fieldset>\r\n    <legend>Add Event</legend>\r\n    <div class="field">\r\n        <label for="summary" class="label">Title<span class="hintanchor" title="Required :: Title of the event"><img src="{$templateinfo.imagedir}help.png" alt="[?]"/></span></label>\r\n        <div class="inputboxwrapper"><input type="text" id="summary" name="summary" size="32" class="inputbox"  onblur="checkElement(''summary'', ''text'', true, 0, 50, '''');" value="{$copyitem.summary}"/><br /><span class="error" id="summaryError">Required: Please enter a title for the event, shorter then 50 characters.</span></div><br />\r\n        \r\n        <label for="sdate" class="label">Starting Date<span class="hintanchor" title="Required :: Date that the event starts"><img src="{$templateinfo.imagedir}help.png" alt="[?]"/></span></label>\r\n        <div class="inputboxwrapper"><input name="sdate" id="sdate" type="text" size="20" maxlength="20" class="inputbox format-y-m-d highlight-days-67 range-low-today" onblur="checkElement(''sdate'', ''date'', true, 0, 0, '''');"/><br /><span class="error" id="sdateError">Required: The date must be in the format: yyyy/mm/dd.</span></div><br />\r\n        \r\n        <label for="stime" class="label">Starting Time<span class="hintanchor" title="Required :: Time that the event starts"><img src="{$templateinfo.imagedir}help.png" alt="[?]"/></span></label>\r\n        <div class="inputboxwrapper">{html_select_time field_array=stime class=inputbox style=width:40px time=0}</div><br />\r\n        \r\n		<label for="edate" class="label">Ending Date<span class="hintanchor" title="Required :: Date that the event ends"><img src="{$templateinfo.imagedir}help.png" alt="[?]"/></span></label>\r\n        <div class="inputboxwrapper"><input name="edate" id="edate" type="text" size="20" maxlength="20" class="inputbox format-y-m-d highlight-days-67 range-low-today" onblur="checkElement(''edate'', ''date'', true, 0, 0, '''');"/><br /><span class="error" id="edateError">Required: The date must be in the format: yyyy/mm/dd.</span></div><br />\r\n\r\n        <label for="etime" class="label">Ending Time<span class="hintanchor" title="Required :: Time that the event ends"><img src="{$templateinfo.imagedir}help.png" alt="[?]"/></span></label>\r\n        <div class="inputboxwrapper">{html_select_time field_array=etime class=inputbox style=width:40px time=0}</div><br />\r\n\r\n        <label for="colour" class="label">Colour<span class="hintanchor" title="Optional :: Colour for the event, will be randomly generated if none is supplied"><img src="{$templateinfo.imagedir}help.png" alt="[?]"/></span></label>\r\n        <div class="inputboxwrapper"><input id="colour" name="colour" type="text" size="13" class="inputbox" onblur="checkElement(''colour'', ''text'', false, 0, 7, '''');"/>&nbsp;<img src="{$templateinfo.imagedir}rainbow.png" alt="[r]" width="16" height="16" class="rain" id="colourSelector" /><br /><span class="error" id="colourError">Optional: Custom colour. If no colour is entered, a random one will be generated.</span></div><br />\r\n            \r\n           <span class="label">Groups<span class="hintanchor" title="Optional :: Groups that can see this event. All groups will have access if no group is selected."><img src="{$templateinfo.imagedir}help.png" alt="[?]"/></span></span>\r\n               <div class="inputboxwrapper"><ul class="checklist">\r\n               <li><label for="group_all"><input type="checkbox" value="1" id="group_all" onchange="checkAll(''group'')">Select/Unselect All</label></li>\r\n           <li><label for="group0"><input type="checkbox" value="1" name="groups[0]" id="group0">Guests</label></li>\r\n            {section name=team loop=$numteams}\r\n                {assign var="id" value=$teams[team].id}\r\n                <li><label for="group{$teams[team].id}"><input type="checkbox" value="1" name="groups[{$teams[team].id}]" id="group{$teams[team].id}">{$teams[team].teamname}</label></li>\r\n            {/section}\r\n            </ul></div><br />\r\n	     \r\n{literal}\r\n<script type="text/javascript">\r\n<!--\r\nfunction showSignup(show)\r\n{\r\n    if (show)\r\n    {\r\n        document.getElementById(''signupDiv'').style.display = "block";\r\n    }\r\n    else\r\n    {\r\n        document.getElementById(''signupDiv'').style.display = "none";\r\n    }\r\n}\r\nfunction changeGroups()\r\n{\r\n    var index = document.getElementById(''signupusers'').selectedIndex;\r\n    var type = document.getElementById(''signupusers'').options[index].value;\r\n    \r\n    if (type == 0 || type == 1)\r\n    {\r\n        document.getElementById(''patrolList'').style.display = ''block'';\r\n        document.getElementById(''memberlist'').style.display = ''none'';        \r\n    }\r\n    else if (type == 2)\r\n    {\r\n        document.getElementById(''patrolList'').style.display = ''none'';        \r\n        document.getElementById(''memberlist'').style.display = ''none'';        \r\n    }\r\n    else if (type == 3)\r\n    {\r\n        document.getElementById(''memberlist'').style.display = ''block'';        \r\n        document.getElementById(''patrolList'').style.display = ''none'';        \r\n    }\r\n}\r\n//-->\r\n</script>\r\n{/literal}\r\n\r\n<span class="label">Enable signups<span class="hintanchor" title="Required :: Can members signup for this event. Once you have added this event you will be have access to more options."><img src="{$templateinfo.imagedir}help.png" alt="[?]"/></span></span><div class="inputboxwrapper"><input type="radio" name="signup" id="signup:yes" value="1" {if $events.signup}checked="checked"{/if} onchange="showSignup(true);"/><label for="signup:yes">Yes</label>&nbsp;<input type="radio" name="signup" id="signup:no" value="0" {if $action == "edit"}{if !$events.signup}checked="checked"{/if}{else}checked="checked"{/if} onchange="showSignup(false);"/><label for="signup:no">No</label></div><br />\r\n\r\n<div id="signupDiv" {if $action == "new" || $events.signup == 0}style="display:none";{/if}>\r\n<label for="signupusers" class="label"><b>Signups</b><span class="hintanchor" title="Who may signup for this event?"><img src="{$templateinfo.imagedir}help.png" alt="[?]"/></span></label>\r\n<div class="inputboxwrapper">  \r\n<select name="signupusers" id="signupusers" class="inputbox" {if $numpatrols > 0 || $nummembers > 0}onchange="changeGroups();"{/if}>\r\n      <option value="0" {if $events.signupusers == 0}selected="selected"{/if}>Members and parents</option>\r\n      <option value="1" {if $events.signupusers == 1}selected="selected"{/if}>Only members</option>\r\n      <option value="2" {if $events.signupusers == 2}selected="selected"{/if}>Only parents</option>\r\n     </select></div><br />\r\n     \r\n{if $numpatrols > 0}<div id="patrolList" {if $action == "edit" && ($events.signupusers == 2 || $events.signupusers == 3)}style="display:none";{/if}><span class="label"><b>{$scoutlang.patrol}</b><span class="hintanchor" title="{$scoutlang.members} from which {$scoutlang.patrol} may signup for this event."><img src="{$templateinfo.imagedir}help.png" alt="[?]"/></span></span>\r\n<div class="inputboxwrapper"><ul class="checklist">\r\n<li><label for="patrol_all"><input type="checkbox" value="1" id="patrol_all" onchange="checkAll(''patrol'')">Select/Unselect All</label></li>\r\n{section name=patrol loop=$numpatrols}\r\n    {assign var="id" value=$patrols[patrol].id}\r\n    <li><label for="patrol{$patrols[patrol].id}"><input type="checkbox" value="1" name="patrols[{$patrols[patrol].id}]" id="patrol{$patrols[patrol].id}" {if $events.patrols.$id == 1}checked="checked"{/if}>{$patrols[patrol].teamname}</label></li>\r\n{/section}\r\n</ul></div><br /></div>\r\n{/if}\r\n</div>\r\n		<label for="story" style="font-weight:bold;">Details<span class="hintanchor" title="Optional :: Extra details regarding the event"><img src="{$templateinfo.imagedir}help.png" alt="[?]"/></span></label>\r\n               <textarea id="story" name="story" style="width: 100%; height: 25em;">{$copyitem.detail}</textarea><br />\r\n<div class="submitWrapper">\r\n               <input name="validation" type="hidden" id="validation" value="summary,text,true,0,50,0;sdate,date,true,0,0,0;edate,date,true,0,0,0" />\r\n        <input name="Submit" type="submit" id="Submit" value="Submit" class="button" /><input type="button" name="Submit2" value="Cancel" onClick="window.location=''index.php?page=mythings''" class="button" />\r\n        </div>\r\n    </fieldset>\r\n	</form>', 37);
#@#INSERT INTO `!#prefix#!pagecontent` (`pageid`, `pagenum`, `content`, `id`) VALUES 
