(25, 1, '<table width="100%" cellpadding="0" cellspacing="0">\r\n<tr valign="top">\r\n<td width="140">\r\n<ul class="left_title">\r\n    {if $cpallowed}<li><a href="index.php?page=usercp&amp;menuid={$menuid}">Control Panel</a></li>{/if}\r\n    {if $profileallowed}<li><a href="index.php?page=profile&amp;menuid={$menuid}">Profile</a></li>{/if}\r\n    {if $contributionallowed}<li><a href="index.php?page=mythings&amp;menuid={$menuid}">Contributions</a></li>{/if}\r\n    {if $groupsallowed}<li><a href="index.php?page=mypatrol&amp;menuid={$menuid}">Groups</a></li>{/if}\r\n    {if $pmallowed}<li class="active"><a href="index.php?page=pmmain&amp;menuid={$menuid}">Private Messages</a></li>{/if}\r\n</ul>\r\n</td>\r\n<td>\r\n{literal}\r\n<script type="text/javascript">\r\n<!--\r\nfunction deletepm() {\r\nif (confirm("This will delete the personal messages. Continue?"))\r\n return true;\r\nelse\r\n    return false;\r\n}\r\n//-->\r\n</script>\r\n{/literal}\r\n<ul class="top_title">\r\n    <li {if $onpage == "Inbox"}class="active"{/if}><a href="index.php?page=pmmain&amp;menuid={$menuid}" class="nolink">Inbox</a></li>\r\n    <li {if $onpage == "Sentbox"}class="active"{/if}><a href="index.php?page=pmmain&amp;action=sentbox&amp;menuid={$menuid}" class="nolink">Sentbox</a></li>\r\n    <li {if $onpage == "Savebox"}class="active"{/if}><a href="index.php?page=pmmain&amp;action=savebox&amp;menuid={$menuid}" class="nolink">Savebox</a></li>\r\n    <li {if $onpage == "Drafts"}class="active"{/if}><a href="index.php?page=pmmain&amp;action=drafts&amp;menuid={$menuid}" class="nolink">Drafts</a></li>\r\n    <li><a href="index.php?page=pmmain&amp;action=typepm&amp;menuid={$menuid}" class="nolink">New PM</a></li>\r\n</ul>\r\n<br style="clear:both;" />\r\n<table cellspacing="0" cellpadding="2" border="0" width="100%">\r\n<tr>\r\n<td align="center" valign="top">\r\n{if $numpm > 0}\r\n<form name="form1" action="{$pageaction}" method="post" onsubmit="return deletepm();">\r\n{literal}\r\n  <script type="text/javascript">\r\n<!--\r\nfunction expand()\r\n{\r\n    document.getElementById(''shortuser'').style.display = ''none'';\r\n    document.getElementById(''longuser'').style.display = '''';\r\n}\r\n\r\nfunction collapse()\r\n{\r\n    document.getElementById(''shortuser'').style.display = '''';\r\n    document.getElementById(''longuser'').style.display = ''none'';\r\n}\r\n//-->\r\n  </script>\r\n  {/literal}\r\n    <table cellspacing="1" cellpadding="2" class="table" width="100%">\r\n     <tr>\r\n     <th class="smallhead" width="2%"></th>\r\n     <th class="smallhead">Subject</th>\r\n     <th class="smallhead" width="20%">{if $onpage != "Drafts" && $onpage != "Sentbox"}From{else}To{/if}</th>\r\n     <th class="smallhead" width="20%">{if $onpage != "Drafts"}Date Sent{else}Date Saved{/if}</th>\r\n     <th class="smallhead" width="6%">Action</th>\r\n     <th class="smallhead" width="6%">Select</th>\r\n     </tr>\r\n     {section name=pmloop loop=$numpm}\r\n     <tr valign="top">\r\n     <td {if $pm[pmloop].readpm == 0 && $onpage != "Drafts"}class="newrow"{else}class="row"{/if} align="center">{if $pm[pmloop].readpm == 0 && $onpage != "Drafts"}<img src="{$templateinfo.imagedir}newpm.gif" alt="New" border="0" />{else}<img src="{$templateinfo.imagedir}oldpm.gif" alt="Read" border="0" />{/if}</td>\r\n      <td {if $pm[pmloop].readpm == 0 && $onpage != "Drafts"}class="newrow"{else}class="row"{/if}><a href="index.php?page=pmmain&amp;action=readpm&amp;id={$pm[pmloop].id}&amp;menuid={$menuid}" title="{$pm[pmloop].text|strip_tags|truncate}">{$pm[pmloop].subject}</a></td>\r\n      <td {if $pm[pmloop].readpm == 0 && $onpage != "Drafts"}class="newrow"{else}class="row"{/if}>{if $onpage != "Drafts" && $onpage != "Sentbox"}<a href="index.php?page=profile&amp;action=view&amp;user={$pm[pmloop].fromuserid}&amp;menuid={$menuid}" title="View User Profile">{$pm[pmloop].fromuser}</a><img {if $pm[pmloop].userstatus == "offline"}src="{$templateinfo.imagedir}offline.png"{elseif $pm[pmloop].userstatus == "online and active"}src="{$templateinfo.imagedir}active.png"{else}src="{$templateinfo.imagedir}inactive.png"{/if} alt="{$pm[pmloop].userstatus}" />{else}\r\n      {if $pm[pmloop].touser.number > 3}\r\n      <div id="shortuser" style="display:'''';">\r\n    {section name=users loop=3}\r\n      <a href="index.php?page=profile&amp;action=view&amp;user={$pm[pmloop].touser.users[users].id}&amp;menuid={$menuid}" title="View User Profile">{$pm[pmloop].touser.users[users].uname}</a><img {if $pm[pmloop].touser.users[users].status == "offline"}src="{$templateinfo.imagedir}offline.png"{elseif $pm[pmloop].touser.users[users].status == "online and active"}src="{$templateinfo.imagedir}active.png"{else}src="{$templateinfo.imagedir}inactive.png"{/if} alt="{$pm[pmloop].touser.users[users].status}" />{if $smarty.section.users.iteration != 3}, {else}<a href="#" onclick="expand();" class="hintanchor">[+]</a>{/if}{/section}\r\n      </div>\r\n      <div id="longuser" style="display:none;">\r\n      {/if}\r\n      {section name=users loop=$pm[pmloop].touser.number}\r\n      <a href="index.php?page=profile&amp;action=view&amp;user={$pm[pmloop].touser.users[users].id}&amp;menuid={$menuid}" title="View User Profile">{$pm[pmloop].touser.users[users].uname}</a><img {if $pm[pmloop].touser.users[users].status == "offline"}src="{$templateinfo.imagedir}offline.png"{elseif $pm[pmloop].touser.users[users].status == "online and active"}src="{$templateinfo.imagedir}active.png"{else}src="{$templateinfo.imagedir}inactive.png"{/if} alt="{$pm[pmloop].touser.users[users].status}" />{if $smarty.section.users.iteration != $pm[pmloop].touser.number}, {elseif $pm[pmloop].touser.number > 3}<a href="#" onclick="collapse();" class="hintanchor">[-]</a>{/if}{/section}{if $pm[pmloop].touser.number > 3}</div>{/if}{/if}</td>\r\n      <td {if $pm[pmloop].readpm == 0 && $onpage != "Drafts"}class="newrow"{else}class="row"{/if} align="center">{$pm[pmloop].date+$timeoffset|date_format_pretty}</td>\r\n      <td {if $pm[pmloop].readpm == 0 && $onpage != "Drafts"}class="newrow"{else}class="row"{/if} align="center">\r\n      {if $onpage == "Inbox"}<a href="index.php?page=pmmain&amp;action=save&amp;id={$pm[pmloop].id}&amp;menuid={$menuid}"><img border="0" src="{$templateinfo.imagedir}move.png" alt="Move to Savebox" /></a>{/if}{if $onpage == "Drafts"}\r\n      <a href="index.php?page=pmmain&amp;action=send&amp;id={$pm[pmloop].id}&amp;menuid={$menuid}"><img border="0" src="{$templateinfo.imagedir}move.png" alt="Send PM" /></a>&nbsp;<a href="index.php?page=pmmain&amp;action=edit&amp;id={$pm[pmloop].id}&amp;menuid={$menuid}"><img border="0" src="{$templateinfo.imagedir}edit.gif" alt="Edit PM" /></a>{/if}</td>\r\n      <td {if $pm[pmloop].readpm == 0 && $onpage != "Drafts"}class="newrow"{else}class="row"{/if} align="center"><input type="checkbox" name="pm_{$pm[pmloop].id}" id="pm_{$pm[pmloop].subject}" value="1" /></td>\r\n     </tr>\r\n     {/section}\r\n     <tr>\r\n     <td colspan="6" class="row" align="right"><input type="submit" class="button" value="Delete Selected Messages" name="delete" id="delete" /><br /><input type="submit" class="button" value="Delete All Messages" name="deleteall" id="deleteall" /></td>\r\n     </tr>\r\n     <tr>\r\n     <td colspan="6" class="row" align="left">\r\n     {$onpage} usage:<br />\r\n        <img src="bar.php?rating={math equation=((x/y)*100) x=$numpm y=$config.numpm}&amp;width=320&amp;height=20&amp;red={$templateinfo.red}&amp;blue={$templateinfo.blue}&amp;green={$templateinfo.green}" alt="You currently using {$numpm} of your {$config.numpm} allowed messages" /><br />\r\n        You are currently have {$numpm} messages in your {$onpage}. You are allowed a maximum of {$config.numpm} messages per box.\r\n    </td>\r\n     </tr>     \r\n    </table>\r\n    <input type="hidden" name="oldpage" id="oldpage" value="{$onpage}" />\r\n    </form>\r\n{else}\r\nThe are no messages in the {$onpage} folder\r\n{/if}\r\n</td>\r\n</tr>\r\n</table></td></tr></table>', 48),
(25, 2, '<table width="100%" cellpadding="0" cellspacing="0">\r\n<tr valign="top">\r\n<td width="140">\r\n<ul class="left_title">\r\n    {if $cpallowed}<li><a href="index.php?page=usercp&amp;menuid={$menuid}">Control Panel</a></li>{/if}\r\n    {if $profileallowed}<li><a href="index.php?page=profile&amp;menuid={$menuid}">Profile</a></li>{/if}\r\n    {if $contributionallowed}<li><a href="index.php?page=mythings&amp;menuid={$menuid}">Contributions</a></li>{/if}\r\n    {if $groupsallowed}<li><a href="index.php?page=mypatrol&amp;menuid={$menuid}">Groups</a></li>{/if}\r\n    {if $pmallowed}<li class="active"><a href="index.php?page=pmmain&amp;menuid={$menuid}">Private Messages</a></li>{/if}\r\n</ul>\r\n</td>\r\n<td>\r\n{literal}\r\n<script type="text/javascript">\r\n<!--\r\nfunction deletepm(articleId, page) {\r\nif (confirm("This will delete the personal message. Continue?")){/literal}\r\ndocument.location = "index.php?page=pmmain&action=delete&id=" + articleId + "&menuid={$menuid}";{literal}\r\n}\r\n//-->\r\n</script>\r\n{/literal}\r\n<ul class="top_title">\r\n    <li><a href="index.php?page=pmmain&amp;menuid={$menuid}" class="nolink">Inbox</a></li>\r\n    <li><a href="index.php?page=pmmain&amp;action=sentbox&amp;menuid={$menuid}" class="nolink">Sentbox</a></li>\r\n    <li><a href="index.php?page=pmmain&amp;action=savebox&amp;menuid={$menuid}" class="nolink">Savebox</a></li>\r\n    <li><a href="index.php?page=pmmain&amp;action=drafts&amp;menuid={$menuid}" class="nolink">Drafts</a></li>\r\n    <li><a href="index.php?page=pmmain&amp;action=typepm&amp;menuid={$menuid}" class="nolink">New PM</a></li>\r\n</ul><br style="clear:both;" />\r\n<table cellspacing="0" cellpadding="2" border="0" width="100%">\r\n<tr>\r\n<th colspan="2" class="normalhead">{$pm.subject}</th>\r\n</tr>\r\n<tr>\r\n<td align="center" valign="top">\r\n    <table cellspacing="1" cellpadding="2" width="100%">\r\n     <tr>\r\n     <td align="left"><span style="font-weight:bold">{if !$drafts && !$sentbox}From:</span> <a href="index.php?page=profile&amp;action=view&amp;user={$pm.fromuserid}&amp;menuid={$menuid}" alt="View User Profile">{$pm.fromuser}</a>{else}To:</span> {section name=users loop=$pm.touser.number}\r\n      <a href="index.php?page=profile&amp;action=view&amp;user={$pm.touser.users[users].id}&amp;menuid={$menuid}" title="View User Profile">{$pm.touser.users[users].uname}</a><img {if $pm.touser.users[users].status == "offline"}src="{$templateinfo.imagedir}offline.png"{elseif $pm.touser.users[users].status == "online and active"}src="{$templateinfo.imagedir}active.png"{else}src="{$templateinfo.imagedir}inactive.png"{/if} alt="{$pm.touser.users[users].status}" />{if $smarty.section.users.iteration != $pm.touser.number}, {/if}{/section}{/if}</td>\r\n     </tr>\r\n     <tr>\r\n     <td align="left"><span style="font-weight:bold">{if !$drafts}Date Sent:{else}Date Saved: {/if}</span> {$pm.date+$timeoffset|date_format_pretty}</td>\r\n     </tr>\r\n     <tr>\r\n     <td>\r\n     {if $pm.type != 4 && $pm.type != 2}\r\n        <input type="button" class="button" value="Reply to message" name="reply" id="reply" onclick="window.location=''index.php?page=pmmain&amp;action=reply&amp;id={$pm.id}&amp;menuid={$menuid}'';" /> &nbsp;\r\n     {/if}\r\n     {if $pm.type == 4}\r\n        <input type="button" class="button" value="Send message" name="send" id="send" onclick="window.location=''index.php?page=pmmain&amp;action=send&amp;id={$pm.id}&amp;menuid={$menuid}'';" /> &nbsp;\r\n        <input type="button" class="button" value="Edit message" name="edit" id="edit" onclick="window.location=''index.php?page=pmmain&amp;action=edit&amp;id={$pm.id}&amp;menuid={$menuid}'';" /> &nbsp;\r\n     {/if}\r\n     <input type="button" class="button" value="Delete message" name="delete" id="delete" onclick="deletepm({$pm.id});" /> &nbsp;\r\n     </td>\r\n     </tr>\r\n     <tr>\r\n     <th class="smallhead">Message</th>\r\n  </tr>\r\n     <tr>\r\n     <td>{$pm.text|bbcode2html}</td>\r\n     </tr>\r\n    </table>\r\n</td>\r\n</tr>\r\n</table></td></tr></table>', 49),
(26, 1, '<form name="relogons" method="post" action="{$action}">\r\n<fieldset>\r\n<legend>You are required to logon to access this page</legend>\r\n<div class="field">\r\n	<label for="username2" class="label">Username</label>\r\n	<div class="inputboxwrapper"><input type="text" name="username2" id="username2" size="50" maxlength="50" class="inputbox" /></div><br />\r\n	<label for="username2" class="label">Password</label>\r\n	<div class="inputboxwrapper"><input type="password" name="password2" id="password2" size="50" maxlength="50" class="inputbox" /></div><br />\r\n</div>\r\n<div class="submitWrapper"><input type="submit" name="relogon" id="relogon" value="Login" class="button" />\r\n<br /><a href="index.php?page=forgot" class="smalltext">I forgot my password!</a>\r\n{if $config.register == 1}\r\n<br /><a href="index.php?page=register" class="smalltext">I''d like to register.</a>\r\n{/if}\r\n</div>\r\n</fieldset>\r\n</form>', 50),
(25, 3, '<table width="100%" cellpadding="0" cellspacing="0">\r\n<tr valign="top">\r\n<td width="140">\r\n<ul class="left_title">\r\n    {if $cpallowed}<li><a href="index.php?page=usercp&amp;menuid={$menuid}">Control Panel</a></li>{/if}\r\n    {if $profileallowed}<li><a href="index.php?page=profile&amp;menuid={$menuid}">Profile</a></li>{/if}\r\n    {if $contributionallowed}<li><a href="index.php?page=mythings&amp;menuid={$menuid}">Contributions</a></li>{/if}\r\n    {if $groupsallowed}<li><a href="index.php?page=mypatrol&amp;menuid={$menuid}">Groups</a></li>{/if}\r\n    {if $pmallowed}<li class="active"><a href="index.php?page=pmmain&amp;menuid={$menuid}">Private Messages</a></li>{/if}\r\n</ul>\r\n</td>\r\n<td>\r\n<script type="text/javascript">\r\n{include file="../../scripts/validator.tpl"}\r\n</script>\r\n<ul class="top_title">\r\n    <li><a href="index.php?page=pmmain&amp;menuid={$menuid}" class="nolink">Inbox</a></li>\r\n    <li><a href="index.php?page=pmmain&amp;action=sentbox&amp;menuid={$menuid}" class="nolink">Sentbox</a></li>\r\n    <li><a href="index.php?page=pmmain&amp;action=savebox&amp;menuid={$menuid}" class="nolink">Savebox</a></li>\r\n    <li><a href="index.php?page=pmmain&amp;action=drafts&amp;menuid={$menuid}" class="nolink">Drafts</a></li>\r\n    <li class="active"><a href="index.php?page=pmmain&amp;action=typepm&amp;menuid={$menuid}" class="nolink">New PM</a></li>\r\n</ul>\r\n<br style="clear:both;" />\r\n\r\n<form method="post" name="posttopic" onsubmit="return checkForm([[''touser'',''text'',true,0,0,''''],[''subject'',''text'',true,0,0,'''']];">   \r\n<fieldset>\r\n<legend>{if !$editmode}New{else}Edit{/if} Personal Message</legend>\r\n<div class="field">\r\n<label for="touser" class="label">To<span class="hintanchor"title="Required :: Enter in the username of the person you want to send this message too. If you want to send it to more then one person, seperate their usernames with a comma (<i>,</i>)"><img src="{$templateinfo.imagedir}help.png" alt="[?]"/></span></label>\r\n<div class="inputboxwrapper"><input type="text" size="50" class="inputbox" name="touser" id="touser" value="{$editpm.touser}"  onblur="checkElement(''touser'', ''text'', true, 0, 0, '''');"/><br /><span class="error" id="touserError">Required</span></div><br />\r\n<label for="subject" class="label">Subject<span class="hintanchor"title="Required :: Enter a subject for this message"><img src="{$templateinfo.imagedir}help.png" alt="[?]"/></span></label>\r\n<div class="inputboxwrapper"><input type="text" size="50" class="inputbox" name="subject" id="subject" value="{$editpm.subject}"  onblur="checkElement(''subject'', ''text'', true, 0, 0, '''');"/><br /><span class="error" id="subjectError">Required</span></div><br />\r\n</div>\r\n\r\n<b>Message<span class="hintanchor" title="Required :: Enter your message here.<br />If you know BBCode, you can "><img src="{$templateinfo.imagedir}help.png" alt="[?]"/></span></b>\r\n<textarea style="width:100%" rows="30" name="story" id="story" class="inputbox">{$editpm.text}</textarea>\r\n\r\n<div class="submitWrapper">\r\n<input type="submit" name="submit" value="Send PM" class="button" />&nbsp;<input type="submit" name="submit" value="Save PM" class="button" />&nbsp;<input type="reset" name="reset" value="Cancel" class="button" onclick="history.go(-1);" />\r\n</div>\r\n</fieldset>\r\n </form></td></tr></table>', 51);
