(27, 1, '{if $numadva > 0 }\r\n    <a name="top"></a>\r\n    <div align="center">\r\n<div align="right">{$scoutlang.award_scheme}: \r\n<script type="text/javascript">\r\n{literal}\r\nfunction changeScheme()\r\n{\r\n    var index = document.getElementById(''schemes'').selectedIndex;\r\n    var id = document.getElementById(''schemes'').options[index].value;\r\n    window.location = "index.php?page=advancements&scheme="+ id\r\n}\r\n{/literal}\r\n</script>\r\n<select id="schemes" class="inputbox" onchange="changeScheme()" style="width:300px;">\r\n{section name=num loop=$numschemes}\r\n    <option value="{$schemes[num].id}" {if $schemeNumber == $schemes[num].id}selected="selected"{/if}>{$schemes[num].name}</option>\r\n{/section}\r\n</select>\r\n</div>\r\n    <div style="border:1px solid #000;text-align:center;width:29%;float:left;">\r\n    {section name=num loop=$numadva}\r\n    <a href="#{$advan[num].advancement|replace:'' '':''''}">{$advan[num].advancement}</a><br />\r\n    {/section}\r\n    </div>\r\n    <table width="70%"  border="0" cellspacing="1" cellpadding="4" class="table">\r\n        {section name=num loop=$numadva}\r\n          {if $advan[num].numitems > 0}\r\n          <tr><th class="normalhead"><a id="{$advan[num].advancement|replace:'' '':''''}" name="{$advan[num].advancement|replace:'' '':''''}"></a>{$advan[num].advancement}</th></tr>\r\n          <tr>\r\n            <th scope="col"  class="smallhead">Requirement</th>\r\n          </tr>\r\n          {section name=numreq loop=$advan[num].numitems}\r\n              <tr valign="top" align="center">\r\n                <td class="row"><strong>{$advan[num].items[numreq].item}</strong><br />\r\n                <span class="comment">{$advan[num].items[numreq].description}</span></td>\r\n              </tr>\r\n          {/section}\r\n          <tr>\r\n          <td class="row" style="text-align:center;">\r\n            <a href="#top">Back to top</a>\r\n          </td>\r\n          </tr>\r\n          {/if}\r\n         {/section}\r\n          <tr>\r\n          <td class="row" style="text-align:center;">\r\n            <a href="index.php?page=advancements">Back to {$scoutlang.award_scheme}</a>\r\n          </td>\r\n          </tr>\r\n    </table>\r\n{else}\r\n<div align="center">\r\n{if $numschemes}\r\n<p>Select a {$scoutlang.award_scheme}.</p>\r\n<ul>\r\n{section name=scheme loop=$numschemes}\r\n<li><a href="index.php?page=advancements&amp;scheme={$schemes[scheme].id}">{$schemes[scheme].name} {$scoutlang.award_scheme}</a></li>\r\n {/section}\r\n </ul>\r\n {else}There are no {$scoutlang.award_scheme} available.{/if}</div>\r\n{/if}', 52),
