(29, 1, '{literal}\r\n  <script type="text/javascript">\r\n<!-- \r\n function changeoptions(type)\r\n {\r\n    var numoptions = document.getElementById(''numoptions'').value;\r\n    var optiondiv = document.getElementById(''optiondiv'');\r\n    var temp = '''';\r\n    var html = '''';\r\n    for(var i=1;i<=numoptions;i++)\r\n    {\r\n        temp = ''''; \r\n        if (document.getElementById(''option'' + i)) \r\n        {\r\n            temp = document.getElementById(''option'' + i).value; \r\n        }\r\n        html = html + ''<label for="option'' + i + ''" class="label">Option '' + i + ''</label><div class="inputboxwrapper"><input type="text" name="option[]" id="option'' + i + ''" size="50"  class="inputbox" value="'' + temp + ''"/>'';\r\n        if (i>1 && i < numoptions)\r\n        {\r\n           {/literal} \r\n            html = html + ''<a href="#" onclick="moveup(''+i+'');" title="Move up"><img src="{$templateinfo.imagedir}small_arrow_up.png" title="[^]" border="0"/></a<a href="#" onclick="movedown(''+i+'');" title="Move down"><img src="{$templateinfo.imagedir}small_arrow.png" title="[v]" border="0"/></a>'';\r\n            {literal}\r\n        }\r\n        else if (i==1 && numoptions>1)\r\n        {\r\n                   {/literal} html = html + ''<a href="#" onclick="movedown(''+i+'');" title="Move down"><img src="{$templateinfo.imagedir}small_arrow.png" title="[v]" border="0"/></a>'';{literal}\r\n        }\r\n        else if (i==numoptions && numoptions>1)\r\n        {\r\n                    {/literal} html = html + ''<a href="#" onclick="moveup(''+i+'');" title="Move up"><img src="{$templateinfo.imagedir}small_arrow_up.png" title="[^]" border="0"/></a>'';{literal}\r\n        }\r\n        html = html + ''</div><br />'';\r\n    }\r\n    optiondiv.innerHTML = '''';\r\n    optiondiv.innerHTML = html;\r\n }\r\n \r\n function addone(type)\r\n {\r\n    document.getElementById(''numoptions'').value++;\r\n    changeoptions(type);\r\n }\r\n \r\n function takeone(type)\r\n {\r\n    var value = document.getElementById(''numoptions'').value;\r\n    \r\n    if (--value == 0)\r\n    {\r\n        document.getElementById(''numoptions'').value = 1;\r\n    }\r\n    else\r\n    {\r\n        document.getElementById(''numoptions'').value--;\r\n    }\r\n    changeoptions(type);\r\n }\r\n \r\nfunction movedown(number)\r\n {\r\n    tobemoved = document.getElementById(''option''+number).value;\r\n    temp = document.getElementById(''option''+(number+1)).value;\r\n    document.getElementById(''option''+(number+1)).value = tobemoved;\r\n    document.getElementById(''option''+number).value = temp;\r\n }\r\n \r\n function moveup(number)\r\n {\r\n    tobemoved = document.getElementById(''option''+number).value;\r\n    temp = document.getElementById(''option''+(number-1)).value;\r\n    document.getElementById(''option''+(number-1)).value = tobemoved;\r\n    document.getElementById(''option''+number).value = temp;\r\n }\r\n//-->\r\n</script>\r\n{/literal}\r\n<script type="text/javascript">\r\n{include file="../../scripts/validator.tpl"}\r\n</script>\r\n<div align="center">\r\n<form name="forms" id="forms" method="post" onsubmit="return checkForm([[''question'',''text'',true,0,0,''''],[''date_stop'',''date'',false,0,0,'''']]);">\r\n<div class="field">\r\n<fieldset class="formlist">\r\n<legend>{if $action == "edit"}Edit{else}Add{/if} Poll</legend>\r\n<label for="question" class="label">Question<span class="hintanchor" title="Required :: The poll question."><img src="{$templateinfo.imagedir}help.png" alt="[?]"/></span></label>\r\n<div class="inputboxwrapper"><input type="text" name="question" id="question"  class="inputbox" {if $action=="edit"}value="{$item.question}"{/if} onblur="checkElement(''question'', ''text'', true, 0, 0, '''');"/><br /><span class="error" id="questionError">Required</span></div><br />\r\n\r\n<label for="date_stop" class="label">Date End<span class="hintanchor" title="Optional :: Date that the poll must end. Leave blank to run the poll forever."><img src="{$templateinfo.imagedir}help.png" alt="[?]"/></span></label>\r\n<div class="inputboxwrapper"><input type="text" name="date_stop" id="date_stop"  class="inputbox format-y-m-d highlight-days-67 range-low-today" {if $action=="edit" && $item.date_stop != 0}value="{$item.date_stop|date_format:"%Y-%m-%d"}"{/if} onblur="checkElement(''date_stop'', ''date'', false, 0, 0, '''');"/><br /><span class="error" id="date_stopError">Must a valid date in the format: YYYY-MM-DD</span></div><br />\r\n\r\n<label for="numoptions" class="label">Number of options<span class="hintanchor" title="Required :: Number of options in the poll."><img src="{$templateinfo.imagedir}help.png" alt="[?]"/></span></label>\r\n<div class="inputboxwrapper"><input type="text" size="10" class="inputbox" name="numoptions" id="numoptions"  onchange="changeoptions({$item.type})" value="{if $numoptions}{$numoptions}{else}1{/if}" /><a href="#" onclick="takeone({$item.type});"><img src="{$templateinfo.imagedir}small_arrow_delete.png" title="[-]" border="0"/></a><a href="#" onclick="addone({$item.type});"><img src="{$templateinfo.imagedir}small_arrow_add.png" title="[+]" border="0"/></a></div><br />\r\n<div id="optiondiv">\r\n{section name=options loop=$numoptions}\r\n<label for="option{$smarty.section.options.iteration}" class="label">Option {$smarty.section.options.iteration}</label>\r\n<div class="inputboxwrapper"><input type="text" name="option[]" id="option{$smarty.section.options.iteration}" size="50"  class="inputbox" value="{$item.options[options]}"/>{if $smarty.section.options.iteration !=1}<a href="#" onclick="moveup({$smarty.section.options.iteration});" title="Move up"><img src="{$templateinfo.imagedir}small_arrow_up.png" title="[^]" border="0"/></a>{/if}{if $smarty.section.options.iteration < ($numoptions)}<a href="#" onclick="movedown({$smarty.section.options.iteration});" title="Move down"><img src="{$templateinfo.imagedir}small_arrow.png" title="[v]" border="0"/></a>{